// Wrasse Fish - Realistic Medium Detail
// 3D printable model with proper wall thickness

// Wall thickness for 3D printing
wall = 1.2;

// Main body dimensions
body_length = 60;
body_height = 25;
body_width = 18;

module wrasse_body() {
    // Main body - elongated oval shape
    union() {
        // Central body
        scale([1.2, 1, 1]) 
            sphere(d = body_height, $fn=32);

        // Tail section - tapered
        translate([body_length/2 - 5, 0, 0])
            scale([1.5, 0.7, 0.7])
                sphere(d = body_height * 0.8, $fn=32);
    }
}

module dorsal_fin() {
    // Dorsal fin - continuous with spines
    translate([0, 0, body_height/2 + wall])
        for(i = [-20:5:15]) {
            translate([i, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = body_height * 0.6, d1 = 3, d2 = 1, $fn=8);
        }
}

module anal_fin() {
    // Anal fin - smaller than dorsal
    translate([5, 0, -body_height/2 - wall])
        mirror([0, 0, 1])
            for(i = [-10:4:10]) {
                translate([i, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h = body_height * 0.4, d1 = 2.5, d2 = 0.8, $fn=8);
            }
}

module pectoral_fin() {
    // Pectoral fins - side fins
    translate([-body_length/4, body_width/2 + wall, -body_height/4])
        rotate([0, 0, 45])
            scale([1.5, 1, 0.3])
                sphere(d = body_height * 0.7, $fn=16);

    mirror([0, 1, 0])
        translate([-body_length/4, body_width/2 + wall, -body_height/4])
            rotate([0, 0, 45])
                scale([1.5, 1, 0.3])
                    sphere(d = body_height * 0.7, $fn=16);
}

module caudal_fin() {
    // Tail fin - crescent shape
    translate([body_length/2 + 5, 0, 0])
        rotate([0, 0, 90])
            difference() {
                scale([1.5, 1, 0.3])
                    sphere(d = body_height * 1.2, $fn=32);
                translate([0, 0, -body_height * 0.6])
                    cube([body_height * 2, body_height * 2, body_height], center=true);
            }
}

module head_features() {
    // Head with protruding lips
    translate([-body_length/2 - 5, 0, 0])
        union() {
            // Main head
            scale([0.8, 0.9, 0.9])
                sphere(d = body_height * 0.8, $fn=32);

            // Lips/mouth
            translate([-body_height * 0.3, 0, -body_height * 0.1])
                rotate([0, 0, -30])
                    scale([0.3, 0.2, 0.15])
                        sphere(d = body_height * 0.6, $fn=16);

            // Eye
            translate([-body_height * 0.15, body_height * 0.25, body_height * 0.15])
                sphere(d = 3, $fn=16);

            translate([-body_height * 0.15, body_height * 0.25, body_height * 0.15])
                sphere(d = 1.5, $fn=16);
        }
}

module scales_pattern() {
    // Simple scale pattern using small spheres
    for(x = [-body_length/2:3:body_length/2 - 5]) {
        for(y = [-body_width/2 + 2:3:body_width/2 - 2]) {
            for(z = [-body_height/2 + 2:3:body_height/2 - 2]) {
                if((x + y + z) % 6 == 0) {
                    translate([x, y, z])
                        sphere(d = 1, $fn=8);
                }
            }
        }
    }
}

// Assembly
union() {
    // Main body parts
    wrasse_body();
    head_features();

    // Fins
    dorsal_fin();
    anal_fin();
    pectoral_fin();
    caudal_fin();

    // Detail
    scales_pattern();
}

// Base for stability
translate([0, 0, -body_height/2 - wall - 2])
    cube([body_length + 10, body_width + 10, 2], center=true);

