// Yak in realistic medium-complexity style
// Scaled to ~70 mm long, printable without support

module yak() {
    // Body dimensions
    body_len = 50;
    body_w   = 25;
    body_h   = 28;

    // Head dimensions
    head_len = 20;
    head_w   = 12;
    head_h   = 14;

    // Leg dimensions
    leg_h    = 18;
    leg_r    = 4;

    // Horn dimensions
    horn_len = 12;
    horn_r   = 2;

    // Tail dimensions
    tail_len = 15;
    tail_r   = 2.5;

    // Build each major part
    union() {
        // Main bulky body
        translate([0, 0, leg_h])
            difference() {
                hull() {
                    // Front bulge
                    translate([-body_len/2 + 5, 0, 0])
                        scale([1.2, 1, 1])
                            sphere(d = body_w, $fn = 16);
                    // Rear bulge
                    translate([body_len/2 - 8, 0, 0])
                        scale([0.9, 1.1, 0.9])
                            sphere(d = body_w, $fn = 16);
                    // Mid section
                    cube([body_len, body_w, body_h], center = true);
                }
                // Small undercut to avoid overhang
                translate([0, 0, -body_h/2 - 1])
                    cube([body_len + 2, body_w + 2, 2], center = true);
            }

        // Head
        translate([-body_len/2 - head_len/2 + 3, 0, leg_h + body_h/2 - 2])
            rotate([0, 20, 0])
            difference() {
                hull() {
                    sphere(d = head_w, $fn = 16);
                    translate([head_len/2, 0, 0])
                        sphere(d = head_w * 0.8, $fn = 16);
                }
                // Snout taper
                translate([head_len/2, 0, 0])
                    rotate([0, 0, 0])
                        cube([head_len, head_w * 0.6, head_h * 0.6], center = true);
            }

        // Horns (curved pair)
        for (side = [-1, 1]) {
            translate([-body_len/2 - head_len/2 + 3, side * head_w/2, leg_h + body_h/2 + 5])
                rotate([30, 0, side * 25])
                rotate_extrude(angle = 90, $fn = 16)
                    translate([horn_r, 0, 0])
                        circle(r = horn_r, $fn = 8);
        }

        // Legs (four)
        for (x = [-body_len/3, body_len/3])
            for (y = [-body_w/3, body_w/3]) {
                translate([x, y, leg_h/2])
                    cylinder(h = leg_h, r1 = leg_r, r2 = leg_r * 0.9, $fn = 12);
            }

        // Tail
        translate([body_len/2 - 2, 0, leg_h + body_h/2 - 5])
            rotate([0, 90, 0])
                cylinder(h = tail_len, r = tail_r, $fn = 12);

        // Ears (small)
        for (side = [-1, 1]) {
            translate([-body_len/2 - head_len/2 + 1, side * head_w/2, leg_h + body_h/2 + 8])
                rotate([0, 0, side * 30])
                scale([0.6, 0.2, 1])
                    sphere(d = 6, $fn = 8);
        }

        // Hump
        translate([body_len/4, 0, leg_h + body_h/2 + 5])
            sphere(d = 15, $fn = 16);

        // Shaggy skirt (simple fringe)
        for (i = [0: 11]) {
            angle = i * 30;
            translate([body_len/2 * cos(angle) * 0.5, body_w/2 * sin(angle) * 0.5, leg_h/2])
                cube([4, 4, leg_h + 2], center = true);
        }
    }
}

// Render the yak
yak();

