                translate([0, 0, leg_height]) 
                    cylinder(h=2, d=5.5);
            }
        }
    }
}

module hump() {
    // Characteristic zebu hump
    translate([body_length/3, 0, body_height/2]) {
        difference() {
            sphere(d=hump_height);
            translate([0, 0, -hump_height/2]) 
                cube([hump_height+2, hump_height+2, hump_height], center=true);
        }
    }
}

module neck_and_head() {
    // Neck connecting to body
    translate([-body_length/2 - neck_length/2, 0, body_height/2 + leg_height/2]) {
        rotate([0, -30, 0]) {
            hull() {
                sphere(d=8);
                translate([-neck_length, 0, 0]) sphere(d=6);
            }

            // Head
            translate([-neck_length - head_length/2, 0, 2]) {
                rotate([0, -20, 0]) {
                    hull() {
                        sphere(d=6);
                        translate([-head_length/2, 0, 0]) sphere(d=5);
                    }

                    // Muzzle
                    translate([-head_length - 2, 0, -1]) {
                        scale([1.5, 0.8, 0.8]) sphere(d=4);
                    }

                    // Horns
                    for(side = [-1, 1]) {
                        translate([-head_length/2, side*3, 3]) {
                            rotate([30*side, 0, 45*side]) {
                                cylinder(h=horn_length, d1=1.5, d2=0.5);
                            }
                        }
                    }

                    // Ears
                    for(side = [-1, 1]) {
                        translate([-head_length + 1, side*3, 2]) {
                            rotate([0, 0, 45*side]) {
                                scale([1, 0.3, 0.7]) sphere(d=ear_size);
                            }
                        }
                    }
                }
            }
        }
    }
}

module tail() {
    // Tail with tuft
    translate([body_length/2 + 2, 0, body_height/2 + leg_height/3]) {
        rotate([20, 0, 15]) {
            hull() {
                sphere(d=3);
                translate([tail_length, 0, -2]) sphere(d=2);
            }
            // Tail tuft
            translate([tail_length, 0, -2]) {
                sphere(d=4);
            }
        }
    }
}

module body() {
    // Main body with realistic proportions
    union() {
        // Torso
        hull() {
            translate([body_length/2, 0, body_height/2 + leg_height/2]) 
                sphere(d=body_width);
