// Yellowhammer (Emberiza citrinella) - medium complexity realistic model
// Designed for 3D printing - no overhangs, 2mm wall thickness
// Overall size approximately 80mm

module yellowhammer() {
    // Body parameters
    body_length = 40;
    body_width = 20;
    body_height = 25;

    // Head parameters
    head_size = 15;
    beak_length = 8;

    // Wing parameters
    wing_length = 30;
    wing_width = 12;

    // Tail parameters
    tail_length = 25;
    tail_width = 18;

    // Leg parameters
    leg_height = 8;
    foot_length = 6;

    // Main body - slightly oval
    translate([0, 0, leg_height]) {
        difference() {
            // Main body shape
            scale([1, 0.8, 0.7])
                sphere(d=body_height);

            // Flatten bottom for printing
            translate([0, 0, -body_height/2])
                cube([body_width*2, body_width*2, body_height], center=true);
        }
    }

    // Head
    translate([body_length/2 - 5, 0, body_height/2 + leg_height]) {
        sphere(d=head_size);

        // Beak
        rotate([0, -20, 0])
            translate([head_size/2, 0, 0])
                scale([1, 0.4, 0.3])
                    cylinder(h=beak_length, d1=8, d2=2);

        // Eye
        translate([head_size/4, head_size/3, 0]) {
            sphere(d=2);
            translate([0, 0, 1.5])
                sphere(d=1);
        }
    }

    // Wings - layered feathers
    for(i = [0:1]) {
        rotate([0, 0, i == 0 ? 20 : -20]) {
            translate([0, i == 0 ? body_width/2 : -body_width/2, body_height/2 + leg_height]) {
                // Primary wing feathers
                for(f = [0:2]) {
                    translate([f*8 - 8, 0, f*2]) {
                        rotate([0, -10 - f*5, 0])
                            scale([0.8 - f*0.1, 1, 0.3])
                                cylinder(h=wing_length - f*5, d=wing_width - f*2);
                    }
                }

                // Wing coverts
                translate([0, 0, 3]) {
                    scale([1, 0.7, 0.3])
                        cylinder(h=wing_length/2, d=wing_width);
                }
            }
        }
    }

    // Tail - graduated feathers
    translate([-body_length/2 - 5, 0, body_height/3 + leg_height]) {
        for(t = [0:4]) {
            rotate([0, 0, (t-2)*15]) {
                translate([0, 0, t*1.5]) {
                    scale([1, 0.8 - t*0.1, 0.3])
                        cylinder(h=tail_length - t*3, d=tail_width - t*2);
                }
            }
        }
    }

    // Legs and feet
    for(side = [-1, 1]) {
        translate([body_length/3, side*body_width/3, 0]) {
            // Leg
            cylinder(h=leg_height, d=3);

            // Foot
            translate([0, side*foot_length/2, -leg_height/2]) {
                rotate([90, 0, 0])
                    cylinder(h=foot_length, d=2.5);

                // Toes
                for(toe = [-1, 0, 1]) {
                    rotate([0, 90, toe*45])
                        translate([0, 0, foot_length/2])
                            cylinder(h=foot_length/2, d=2);
                }
            }
        }
    }

    // Chest markings
    translate([body_length/4, 0, body_height/3 + leg_height]) {
        for(mark = [0:3]) {
            rotate([0, 0, mark*90])
                translate([0, mark*2, 0])
                    scale([0.3, 1, 0.1])
                        cylinder(h=body_width/2, d=body_height/4);
        }
    }
}

// Render the bird
yellowhammer();

