    // Body segments
    union() {
        // Head
        head();

        // Thorax
        thorax();

        // Abdomen segments
        abdomen();

        // Wings
        wings();

        // Legs
        legs();

        // Antennae
        antennae();
    }
}

// Head module
module head() {
    translate([0, 0, 15]) {
        difference() {
            // Main head shape
            scale([1.2, 1, 1]) sphere(4);

            // Eye sockets
            translate([2.5, 2, 1.5]) sphere(1.2);
            translate([2.5, -2, 1.5]) sphere(1.2);

            // Mandible cutouts
            translate([3.5, 1.5, -2]) rotate([0, 0, 30]) cylinder(2, 1, 0.5);
            translate([3.5, -1.5, -2]) rotate([0, 0, -30]) cylinder(2, 1, 0.5);
        }

        // Eyes
        translate([3.2, 2.2, 1.5]) sphere(0.8);
        translate([3.2, -2.2, 1.5]) sphere(0.8);

        // Mandibles
        translate([4, 1.5, -1]) rotate([0, 0, 30]) cylinder(2, 0.8, 0.3);
        translate([4, -1.5, -1]) rotate([0, 0, -30]) cylinder(2, 0.8, 0.3);
    }
}

// Thorax module
module thorax() {
    translate([0, 0, 8]) {
        // Main thorax
        scale([1.5, 1.2, 1]) sphere(4);

        // Thorax segments
        for(i = [0:2]) {
            rotate([0, 0, i * 120]) {
                translate([3, 0, 0]) scale([2, 0.8, 0.5]) sphere(1);
            }
        }
    }
}

// Abdomen module
module abdomen() {
    // First segment
    translate([0, 0, 2]) cylinder(5, 4, 3.5);

    // Second segment
    translate([0, 0, -1]) cylinder(5, 3.5, 3);

    // Third segment with stripes
    translate([0, 0, -4]) {
        difference() {
            cylinder(5, 3, 2.5);
            // Stripes
            for(i = [1:4]) {
                translate([0, 0, i * 1.2 - 2.5]) {
                    scale([1.05, 1.05, 0.3]) cylinder(0.5, 3, 2.5);
                }
            }
        }
    }

    // Stinger
    translate([0, 0, -7.5]) cylinder(2, 0.5, 0.2);
}

// Wings module
module wings() {
    // Left wing pair
    translate([-1, 3, 10]) {
        rotate([0, 30, 0]) {
            scale([2, 0.1, 1]) sphere(2);
            translate([1, 0, -1]) scale([1.5, 0.08, 0.8]) sphere(1.5);
        }
    }

    // Right wing pair
    mirror([0, 1, 0]) {
        translate([-1, 3, 10]) {
            rotate([0, 30, 0]) {
                scale([2, 0.1, 1]) sphere(2);
                translate([1, 0, -1]) scale([1.5, 0.08, 0.8]) sphere(1.5);
            }
        }
    }
}

// Legs module
module legs() {
    // Left legs
    for(i = [0:2]) {
        rotate([0, 0, i * 120 - 60]) {
            translate([2, 2, 6 - i * 1.5]) {
                rotate([30, 0, -20]) {
                    cylinder(3, 0.4, 0.3);
                    translate([2.5, 0, 0]) rotate([30, 0, 0]) cylinder(2.5, 0.3, 0.2);
                    translate([4.5, 0, 0.5]) rotate([20, 0, 0]) cylinder(2, 0.2, 0.15);
                }
            }
        }
    }

    // Right legs
    mirror([0, 1, 0]) {
        for(i = [0:2]) {
            rotate([0, 0, i * 120 - 60]) {
                translate([2, 2, 6 - i * 1.5]) {
                    rotate([30, 0, -20]) {
                        cylinder(3, 0.4, 0.3);
                        translate([2.5, 0, 0]) rotate([30, 0, 0]) cylinder(2.5, 0.3, 0.2);
                        translate([4.5, 0, 0.5]) rotate([20, 0, 0]) cylinder(2, 0.2, 0.15);
                    }
                }
            }
        }
    }
}

// Antennae module
module antennae() {
    // Left antenna
    translate([0, 1.5, 18]) {
        rotate([30, 0, 0]) {
            cylinder(3, 0.2, 0.15);
            translate([0, 0, 3]) rotate([20, 0, 0]) cylinder(2, 0.15, 0.1);
        }
    }

    // Right antenna
    translate([0, -1.5, 18]) {
        rotate([30, 0, 0]) {
            cylinder(3, 0.2, 0.15);
            translate([0, 0, 3]) rotate([20, 0, 0]) cylinder(2, 0.15, 0.1);
        }
    }
}

