// Realistic Agama Lizard - Medium Complexity
// 3D printable model with anatomical features

// Global parameters
body_length = 60;
body_height = 12;
body_width = 18;
head_length = 15;
head_width = 12;
tail_segments = 15;
leg_thickness = 5;

module agama_body() {
    // Main torso - elongated ellipsoid
    difference() {
        scale([1.8, 1, 0.8])
            sphere(d=body_width, $fn=32);

        // Neck transition cut
        translate([body_length/2 - 5, 0, -body_height/2])
            cube([10, body_width, body_height], center=true);
    }
}

module agama_head() {
    // Triangular head shape
    union() {
        // Snout
        translate([body_length/2 + head_length/2, 0, 0])
            scale([1.5, 1, 0.7])
                sphere(d=head_width, $fn=16);

        // Head base
        translate([body_length/2, 0, 0])
            scale([1, 1.2, 0.9])
                sphere(d=head_width, $fn=16);

        // Eye ridges
        for(side = [-1, 1]) {
            translate([body_length/2 + 3, side * head_width/3, 2])
                rotate([0, 30, 0])
                    scale([0.5, 1, 0.3])
                        sphere(d=3, $fn=8);
        }
    }
}

module agama_eyes() {
    // Eyes with pupils
    for(side = [-1, 1]) {
        translate([body_length/2 + 5, side * head_width/3, 1.5])
            color("black")
                sphere(d=2, $fn=16);

        translate([body_length/2 + 5.5, side * head_width/3, 1.7])
            color("white")
                sphere(d=0.8, $fn=8);
    }
}

module agama_limbs() {
    // Front legs
    for(side = [-1, 1]) {
        // Upper arm
        translate([body_length/4, side * body_width/2, -2])
            rotate([45, 0, 30 * side])
                cylinder(h=8, d1=leg_thickness, d2=leg_thickness-1, $fn=8);

        // Lower arm
        translate([body_length/4 - 2, side * (body_width/2 + 3), -6])
            rotate([0, 0, -30 * side])
                cylinder(h=6, d1=leg_thickness-1, d2=leg_thickness-2, $fn=8);

        // Front foot
        translate([body_length/4 - 4, side * (body_width/2 + 5), -8])
            scale([1.5, 1, 0.5])
                sphere(d=3, $fn=8);
    }

    // Back legs (larger)
    for(side = [-1, 1]) {
        // Upper leg
        translate([-body_length/4, side * body_width/2, -2])
            rotate([30, 0, -20 * side])
                cylinder(h=10, d1=leg_thickness+1, d2=leg_thickness, $fn=8);

        // Lower leg
        translate([-body_length/4 - 3, side * (body_width/2 + 4), -7])
            rotate([0, 0, 20 * side])
                cylinder(h=8, d1=leg_thickness, d2=leg_thickness-1, $fn=8);

        // Back foot
        translate([-body_length/4 - 6, side * (body_width/2 + 6), -10])
            scale([2, 1.2, 0.5])
                sphere(d=4, $fn=8);
    }
}

module agama_tail() {
    // Segmented tail tapering to a point
    for(i = [0:tail_segments-1]) {
        translate([-body_length/2 - i * 3, 0, 0])
            rotate([0, 5 * sin(i*30), 0])
                scale([1, 1 - i*0.05, 1 - i*0.05])
                    sphere(d=body_width - i*1.2, $fn=16);
    }
}

module agama_scales() {
    // Dorsal ridge scales
    for(i = [0:8]) {
        translate([body_length/2 - 5 - i * 5, 0, body_height/2])
            rotate([0, 0, 0])
                scale([0.3, 1, 1.5])
                    cylinder(h=2, d1=2, d2=0.5, $fn=6);
    }

    // Lateral body scales
    for(side = [-1, 1]) {
        for(i = [0:5]) {
            translate([body_length/2 - 10 - i * 6, side * body_width/2, 0])
                rotate([0, 90, 0])
                    scale([0.5, 0.5, 0.5])
                        sphere(d=2, $fn=6);
        }
    }
}

// Main assembly
union() {
    agama_body();
    agama_head();
    agama_eyes();
    agama_limbs();
    agama_tail();
    agama_scales();
}

// Flat base for 3D printing stability
translate([-body_length/2 - tail_segments*3, 0, -10])
    cube([body_length + tail_segments*3 + head_length, body_width*2, 2], center=false);

