// Aardvark model - medium complexity, realistic style
// Overall dimensions: ~80mm long

// Body parameters
body_length = 45;
body_height = 25;
body_width = 20;

// Head parameters
head_length = 20;
head_height = 15;
head_width = 12;

// Snout parameters
snout_length = 25;
snout_diameter = 6;

// Leg parameters
leg_height = 20;
leg_width = 6;
leg_depth = 8;

// Ear parameters
ear_length = 8;
ear_width = 6;
ear_thickness = 2;

// Tail parameters
tail_length = 30;
tail_diameter = 5;

// Helper module for rounded box
module rounded_box(w, h, d, r) {
    translate([0, r, 0])
    hull() {
        for(x = [-w/2+r, w/2-r])
        for(z = [-d/2+r, d/2-r]) {
            translate([x, 0, z])
            sphere(r);
        }
    }
    cube([w-2*r, h-r, d], center=true);
    translate([0, h-r, 0])
    hull() {
        for(x = [-w/2+r, w/2-r])
        for(z = [-d/2+r, d/2-r]) {
            translate([x, 0, z])
            sphere(r);
        }
    }
}

// Body module
module body() {
    union() {
        // Main body
        color("tan")
        rounded_box(body_width, body_height, body_length, 5);

        // Back hump
        translate([0, body_height/2, -body_length/4])
        rotate([0, 0, 0])
        scale([1, 0.8, 0.6])
        sphere(body_height/1.5);
    }
}

// Head module
module head() {
    union() {
        // Main head
        color("tan")
        translate([0, body_height/2 + 5, body_length/2 + head_length/2])
        scale([1, 0.8, 1.2])
        sphere(head_height/1.2);

        // Snout
        color("tan")
        translate([0, body_height/2 + 5 - head_height/4, body_length/2 + head_length + snout_length/2])
        rotate([20, 0, 0])
        cylinder(h=snout_length, d=snout_diameter, $fn=16);

        // Nose tip
        translate([0, body_height/2 + 5 - head_height/4 - 3, body_length/2 + head_length + snout_length])
        sphere(snout_diameter/1.5);

        // Ears
        for(side = [-1, 1]) {
            color("tan")
            translate([side * head_width/2, body_height/2 + 5 + head_height/2, body_length/2 + head_length/2])
            rotate([0, -30, side * 30])
            scale([1, 0.7, 0.3])
            cylinder(h=ear_length, d=ear_width, $fn=16);
        }

        // Eyes
        for(side = [-1, 1]) {
            color("black")
            translate([side * head_width/3, body_height/2 + 5 - head_height/4, body_length/2 + head_length/1.5])
            sphere(1.5);
        }
    }
}

// Leg module
module leg() {
    union() {
        // Upper leg
        color("tan")
        cylinder(h=leg_height/2, d1=leg_width+2, d2=leg_width, $fn=16);

        // Lower leg
        translate([0, 0, leg_height/2])
        cylinder(h=leg_height/2, d1=leg_width, d2=leg_width-1, $fn=16);

        // Foot
        translate([0, -leg_depth/4, leg_height])
        scale([1.2, 1, 0.8])
        sphere(leg_width/1.2);
    }
}

// Tail module
module tail() {
    color("tan")
    translate([0, body_height/2, -body_length/2 - tail_length/2])
    rotate([0, 20, 0])
    cylinder(h=tail_length, d1=tail_diameter, d2=tail_diameter/2, $fn=16);
}

// Main assembly
union() {
    // Body
    body();

    // Head and snout
    head();

    // Legs
    for(pos = [[-body_width/3, 0, body_length/3], 
               [body_width/3, 0, body_length/3],
               [-body_width/3, 0, -body_length/3],
               [body_width/3, 0, -body_length/3]]) {
        translate(pos)
        translate([0, -body_height/2 - leg_height/2, 0])
        leg();
    }

    // Tail
    tail();
}

