// Agouti - medium complexity realistic style
// Designed for 3D printing - 80mm length

// Main dimensions
body_length = 70;
body_height = 35;
head_length = 20;
head_height = 18;
leg_height = 25;
tail_length = 12;

// Body
module body() {
    union() {
        // Main torso
        translate([0, 0, body_height/2])
            scale([1, 0.6, 0.5])
            sphere(d=body_height);

        // Rump
        translate([-body_length/2, 0, body_height/2])
            scale([1.2, 0.7, 0.4])
            sphere(d=body_height);
    }
}

// Head
module head() {
    translate([body_length/2 + head_length/2, 0, body_height/2 + head_height/4]) {
        union() {
            // Skull
            scale([1.3, 0.9, 0.8])
            sphere(d=head_height);

            // Snout
            translate([head_length/2, 0, -head_height/4])
                scale([0.8, 0.6, 0.5])
                sphere(d=head_height);

            // Ears
            for(side = [-1, 1]) {
                translate([0, side*head_height/2, head_height/2])
                    rotate([0, 15*side, 0])
                    scale([0.3, 0.1, 0.6])
                    cylinder(h=head_height/2, d=head_height/3);
            }

            // Nose
            translate([head_length/2 + 2, 0, -head_height/4])
                sphere(d=3);

            // Eyes
            for(side = [-1, 1]) {
                translate([head_length/2 - 2, side*head_height/3, 0])
                    sphere(d=2.5);
            }
        }
    }
}

// Legs
module legs() {
    for(side = [-1, 1]) {
        // Front legs
        translate([body_length/3, side*body_height/4, 0]) {
            union() {
                // Upper leg
                cylinder(h=leg_height/2, d=leg_height/4);
                // Lower leg
                translate([0, 0, leg_height/2])
                    cylinder(h=leg_height/2, d=leg_height/5);
                // Paw
                translate([0, 0, leg_height])
                    sphere(d=leg_height/4);
            }
        }

        // Rear legs
        translate([-body_length/3, side*body_height/4, 0]) {
            rotate([10, 0, 0])
            union() {
                // Upper leg
                cylinder(h=leg_height/2, d=leg_height/4);
                // Lower leg
                translate([0, 0, leg_height/2])
                    cylinder(h=leg_height/2, d=leg_height/5);
                // Paw
                translate([0, 0, leg_height])
                    sphere(d=leg_height/4);
            }
        }
    }
}

// Tail
module tail() {
    translate([-body_length/2 - tail_length/2, 0, body_height/2]) {
        rotate([0, -20, 0])
            scale([1, 0.7, 0.7])
            cylinder(h=tail_length, d=tail_length);
    }
}

// Whiskers
module whiskers() {
    for(side = [-1, 1]) {
        for(i = [1:3]) {
            translate([body_length/2 + head_length/2 + 2, side*head_height/3, -head_height/4 + i*2])
                rotate([0, side*15, 0])
                cylinder(h=5, d=0.5);
        }
    }
}

// Assembly
union() {
    body();
    head();
    legs();
    tail();
    whiskers();
}

