// Main module to create the entire albatross
module albatross() {
    union() {
        // Body
        body();

        // Head and beak
        translate([15, 0, 8]) head();

        // Wings
        translate([-5, 0, 5]) wings();

        // Tail
        translate([-18, 0, 3]) tail();

        // Legs and feet
        translate([5, 0, -2]) legs();
    }
}

// Body module - streamlined oval shape
module body() {
    // Main body
    scale([1.8, 1, 1]) sphere(8);

    // Neck transition
    translate([10, 0, 6]) 
        scale([1.2, 0.8, 0.8]) 
        sphere(6);
}

// Head and beak module
module head() {
    union() {
        // Head
        sphere(5);

        // Beak
        translate([6, 0, -2]) 
            rotate([0, -15, 0]) 
            scale([1.5, 0.4, 0.4]) 
            cylinder(h=12, r1=3, r2=1);

        // Eye
        translate([2, 3.5, 1.5]) 
            sphere(1);

        // Eye socket
        translate([2, 3.5, 1.5]) 
            difference() {
                sphere(1.2);
                sphere(1.1);
            }
    }
}

// Wings module with feather details
module wings() {
    // Left wing
    translate([0, -15, 0]) 
        rotate([0, 0, 20]) 
        wing_half();

    // Right wing
    mirror([0, 1, 0]) 
        translate([0, -15, 0]) 
        rotate([0, 0, 20]) 
        wing_half();
}

// Single wing half
module wing_half() {
    union() {
        // Main wing structure
        translate([20, -15, 0]) 
            rotate([0, 0, -30]) 
