// Main fish body
module anchovy_body() {
    hull() {
        // Head section
        translate([-8, 0, 0]) 
        scale([1.2, 0.6, 0.4])
        sphere(r=4);

        // Middle body section
        translate([0, 0, 0])
        scale([1.5, 0.4, 0.3])
        sphere(r=3);

