    }
}

// Tail fin
module tail_fin() {
    translate([9.5, 0, 0])
    rotate([0, 0, 90])
    scale([0.8, 1.5, 0.1])
    difference() {
        cylinder(h=0.5, r=2, center=true);
        translate([0, 1.5, 0])
        cube([4, 3, 1], center=true);
    }
}

// Dorsal fin
module dorsal_fin() {
    translate([-2, 0, 2])
    rotate([90, 0, 0])
    scale([0.6, 1.2, 0.1])
    difference() {
        cylinder(h=0.4, r=1.5, center=true);
        translate([0, -1, 0])
        cube([3, 2, 1], center=true);
    }
}

