// Realistic Asp - Medium Complexity
// Designed for 3D printing, approximately 80mm length

// Global parameters
body_length = 80;
body_diameter = 8;
head_length = 12;
head_diameter = 10;
tail_taper = 15;
scale_factor = 1;

// Main asp body
module asp_body() {
    union() {
        // Head
        translate([body_length - head_length/2, 0, 0])
            rotate([0, -15, 0])
            scale([1.2, 1, 1.1])
            sphere(d=head_diameter, $fn=20);

        // Body segments with taper
        for(i = [0:4]) {
            translate([i * (body_length - head_length - tail_taper)/5, 0, 0])
                scale([1 - i*0.15, 1 - i*0.12, 1 - i*0.12])
                cylinder(h=(body_length - head_length - tail_taper)/5, d=body_diameter, $fn=16);
        }

        // Tail taper
        translate([body_length - head_length - tail_taper/2, 0, 0])
            cylinder(h=tail_taper, d1=body_diameter*0.85, d2=2, $fn=12);
    }
}

// Eyes with proper positioning
module eyes() {
    for(side = [-1, 1]) {
        translate([body_length - head_length*0.7, side * head_diameter/3, head_diameter/4])
            rotate([0, 90, 0])
            difference() {
                cylinder(h=head_diameter/3, d=head_diameter/4, $fn=12);
                translate([0, 0, -0.1])
                    cylinder(h=head_diameter/3 + 0.2, d=head_diameter/6, $fn=10);
            }
    }
}

// Hood structure (cobra-like)
module hood() {
    translate([body_length - head_length*0.8, 0, 0])
        rotate([0, -20, 0])
        difference() {
            // Main hood
            scale([1.5, 3, 0.5])
            sphere(d=head_diameter*1.8, $fn=20);

            // Cutout for neck
            translate([0, 0, -head_diameter/2])
                cylinder(h=head_diameter, d=head_diameter*0.8, $fn=16);

            // Eye cutouts
            for(side = [-1, 1]) {
                translate([0, side * head_diameter/2, 0])
                    sphere(d=head_diameter/2, $fn=12);
            }
        }
}

// Body pattern (scales)
module scales() {
    for(i = [1:12]) {
        rotate([0, 0, i * 30])
        translate([body_length/2 - i*3, 0, body_diameter/2 + 0.5])
            scale([0.8, 0.4, 0.2])
            sphere(d=body_diameter/2, $fn=8);
    }
}

// Tongue
module tongue() {
    translate([body_length - head_length + 2, 0, -head_diameter/3])
        rotate([0, 90, 0])
        union() {
            // Base
            cylinder(h=head_length/2, d=head_diameter/6, $fn=8);

            // Forked end
            for(side = [-1, 1]) {
                translate([head_length/2 - 0.5, side * head_diameter/10, 0])
                    cylinder(h=head_length/4, d=head_diameter/12, $fn=6);
            }
        }
}

// Assembly
union() {
    // Main body
    asp_body();

    // Hood (only on cobras, but adds realism)
    hood();

    // Eyes
    color("black") eyes();

    // Tongue
    color("red") tongue();

