// Realistic Anole Lizard - Medium Complexity
// Approximate size: 75mm length

$fn = 32;

// Body parameters
body_length = 40;
body_height = 12;
body_width = 18;

// Head parameters
head_length = 15;
head_width = 12;
head_height = 10;

// Tail parameters
tail_segments = 8;
tail_base_width = 10;
tail_base_height = 8;

// Leg parameters
leg_length = 20;
leg_width = 4;
leg_height = 3;
foot_length = 8;
toe_count = 5;

// Dewlap parameters
dewlap_radius = 6;
dewlap_thickness = 2;

module anole_body() {
    // Main body - elongated ellipsoid
    scale([1.2, 0.8, 0.6])
    sphere(d = body_length);

    // Ribbed texture
    for(i = [0:4]) {
        translate([i*6 - 12, 0, body_height/2])
        rotate([90, 0, 0])
        cylinder(h = body_width*0.8, d1 = 2, d2 = 2.5);
    }
}

module anole_head() {
    // Snout
    translate([body_length/2 + head_length/2, 0, 0])
    scale([1.5, 1, 1])
    sphere(d = head_length);

    // Eye sockets
    difference() {
        translate([body_length/2 + head_length/2, 0, head_height/3])
        sphere(d = head_width);

        // Left eye
        translate([body_length/2 + head_length - 3, -head_width/3, head_height/3])
        rotate([0, 30, 0])
        sphere(d = 4);

        // Right eye
        translate([body_length/2 + head_length - 3, head_width/3, head_height/3])
        rotate([0, -30, 0])
        sphere(d = 4);
    }

    // Eyes
    translate([body_length/2 + head_length - 2.5, -head_width/3, head_height/3])
    sphere(d = 3.5);

    translate([body_length/2 + head_length - 2.5, head_width/3, head_height/3])
    sphere(d = 3.5);

    // Nose
    translate([body_length/2 + head_length + 5, 0, 0])
    sphere(d = 3);
}

module anole_tail() {
    // Segmented tail tapering
    for(i = [0:tail_segments-1]) {
        translate([-body_length/2 - i*5, 0, 0])
        scale([1, 1 - i*0.1, 1 - i*0.1])
        sphere(d = tail_base_width - i);
    }
}

module anole_leg(position, angle, side) {
    translate(position)
    rotate([0, angle, 0]) {
        // Upper leg
        rotate([0, -side*20, 0])
        cylinder(h = leg_length/2, d1 = leg_width + 2, d2 = leg_width);

        // Lower leg
        translate([0, 0, leg_length/2])
        rotate([0, side*30, 0])
        cylinder(h = leg_length/2, d1 = leg_width, d2 = leg_width - 1);

        // Foot
        translate([0, 0, leg_length])
        rotate([90, 0, 0])
        cylinder(h = foot_length, d1 = leg_width - 1, d2 = 2);

        // Toes
        for(toe = [0:toe_count-1]) {
            angle = (toe - 2) * 15;
            translate([0, sin(angle) * 3, foot_length])
            rotate([0, angle, 90])
            cylinder(h = 5, d1 = 1.5, d2 = 0.8);
        }
    }
}

module anole_dewlap() {
    // Throat dewlap
    translate([body_length/2 - 5, 0, -body_height/2])
    rotate([0, 0, 90])
    sphere(d = dewlap_radius);

    // Support structure
    translate([body_length/2 - 5, 0, -body_height/2])
    cylinder(h = dewlap_thickness, d = dewlap_radius);
}

// Main assembly
union() {
    // Body
    anole_body();

    // Head
    anole_head();

    // Tail
    anole_tail();

    // Dewlap
    anole_dewlap();

    // Front legs
    anole_leg([body_length/3, body_width/2, -body_height/3], 30, 1);
    anole_leg([body_length/3, -body_width/2, -body_height/3], 30, -1);

    // Back legs
    anole_leg([-body_length/3, body_width/2, -body_height/3], -30, 1);
    anole_leg([-body_length/3, -body_width/2, -body_height/3], -30, -1);

    // Toes on ground for support
    translate([body_length/3, body_width/2, -leg_length - 5])
    sphere(d = 2);
    translate([body_length/3, -body_width/2, -leg_length - 5])
    sphere(d = 2);
    translate([-body_length/3, body_width/2, -leg_length - 5])
    sphere(d = 2);
    translate([-body_length/3, -body_width/2, -leg_length - 5])
    sphere(d = 2);
}

