// Approx 75 mm long, 40 mm high, 35 mm wide
// Medium complexity, no overhangs, 1 mm wall thickness assumed

// Main body module
module body() {
    // Torso – elongated sphere
    translate([0,0,15]) scale([1.5,1,1]) sphere(15, $fn=30);
    // Belly – flattened sphere to avoid overhang
    translate([0,0,8]) scale([1.5,1,0.6]) sphere(15, $fn=30);
}

// Head module
module head() {
    union() {
        // Main head shape
        translate([-22,0,22]) scale([1.2,1,1.1]) sphere(12, $fn=30);
        // Muzzle
        translate([-33,0,18]) scale([1.5,0.9,0.9]) sphere(8, $fn=30);
    }
}

// Ears – two small spheres
module ears() {
    for(side=[-1,1])
        translate([-25,side*10,32]) sphere(4, $fn=20);
}

// Eyes – small spheres on muzzle
module eyes() {
    color("black")
    for(side=[-1,1])
        translate([-32,side*5,22]) sphere(2, $fn=20);
}

// Nose – small flattened sphere
module nose() {
    color("black")
    translate([-38,0,19]) scale([1,0.8,0.5]) sphere(3, $fn=20);
}

// Tail – flat paddle-like tail
module tail() {
    translate([20,0,8]) rotate([0,90,0]) {
        difference() {
            scale([1,2.5,0.5]) sphere(10, $fn=30); // main tail
            translate([-10,0,-2]) cube([20,20,4], center=true); // flatten bottom
        }
    }
}

// Front paw module
module front_paw() {
    union() {
        // Upper arm
        translate([-5,0,12]) rotate([0,-15,0]) cylinder(h=12, r1=4, r2=3, $fn=20);
        // Lower arm
        translate([-12,0,8]) rotate([0,-15,0]) cylinder(h=10, r1=3, r2=2, $fn=20);
        // Paw
        translate([-18,0,6]) sphere(3, $fn=20);
    }
}

// Rear paw module
module rear_paw() {
    union() {
        // Thigh
        translate([12,0,8]) rotate([0,15,0]) cylinder(h=10, r1=5, r2=4, $fn=20);
        // Lower leg
        translate([18,0,5]) rotate([0,15,0]) cylinder(h=8, r1=4, r2=3, $fn=20);
        // Paw
        translate([22,0,3]) sphere(4, $fn=20);
    }
}

// Teeth – two small cubes
module teeth() {
    color("white")
    for(side=[-1,1])
        translate([-36,side*2,14]) cube([2,1,2]);
}

// Whiskers – thin cylinders
module whiskers() {
    for(side=[-1,1])
        for(angle=[-30,0,30])
