        mandible(-1);

        // Eyes
        module eye(side) {
            color("black")
            translate([side * 7, 6, 37])
            sphere(r=3);
        }

        eye(1);
        eye(-1);

        // Antennae
        module antenna(side) {
            translate([side * 6, 4, 40])
            rotate([0, 30, side * 20])
            cylinder(r1=1, r2=0.5, h=15);

            translate([side * 6, 4, 55])
            rotate([0, 30, side * 20])
            sphere(r=1.5);
        }

        antenna(1);
        antenna(-1);

        // Elytra (wing covers)
        module elytron(side) {
            color("saddlebrown")
            translate([side * 10, 0, 15])
            rotate([0, 0, side * 10])
            scale([0.8, 1.2, 0.3])
            cylinder(r1=15, r2=8, h=20);
        }

        elytron(1);
        elytron(-1);

        // Legs
        module leg(side, segment, angle) {
            rotate([0, 0, angle])
            translate([side * 15, 0, 20 - segment * 3])
            rotate([0, 0, side * 30])
            cylinder(r=2, h=8);

            translate([side * 15, 0, 20 - segment * 3])
            rotate([0, 0, side * 30])
            translate([0, 0, 8])
            rotate([side * 20, 0, 0])
            cylinder(r=1.5, h=6);

            translate([side * 15, 0, 20 - segment * 3])
            rotate([0, 0, side * 30])
            translate([0, 0, 14])
            rotate([side * 20, 0, 0])
            translate([0, 0, 6])
            scale([0.8, 0.8, 2])
            sphere(r=1.5);
        }

        // Front legs
        leg(1, 0, 30);
        leg(-1, 0, -30);

        // Middle legs
        leg(1, 1, 0);
        leg(-1, 1, 0);

        // Rear legs
        leg(1, 2, -30);
        leg(-1, 2, 30);

        // Segmentation lines on abdomen
        for (i = [1:3]) {
            color("black")
            translate([0, 0, 10 + i * 5])
            rotate([90, 0, 0])
            scale([1.2, 0.8, 0.05])
            cylinder(r=20, h=0.5);
        }

        // Small bumps on elytra
        for (i = [0:5]) {
            angle = i * 60;
            color("darkgreen")
            translate([10 * cos(angle), 10 * sin(angle), 15])
            sphere(r=1);
            translate([-10 * cos(angle), 10 * sin(angle), 15])
            sphere(r=1);
        }
    }
}

