$fn = 32;

// Adjustable scale for printing
scale_factor = 0.5;  // 1.0 = ~100mm length

// Main body parameters
body_length = 100 * scale_factor;
body_height = 25 * scale_factor;
body_width = 40 * scale_factor;

// Head parameters
head_length = 25 * scale_factor;
head_width = 20 * scale_factor;
head_height = 15 * scale_factor;

// Tail parameters
tail_length = 60 * scale_factor;
tail_segments = 8;

// Leg parameters
leg_height = 20 * scale_factor;
leg_width = 8 * scale_factor;

// Armored shell parameters
shell_segments = 9;
segment_width = body_width * 1.1;
segment_height = 8 * scale_factor;

module armadillo() {
    union() {
        // Central body with armored shell
        translate([0, 0, leg_height]) {
            // Main body core
            color("saddlebrown")
            difference() {
                scale([1, 0.7, 0.5])
                sphere(d = body_width);

                // Hollow for printing
                scale([0.8, 0.6, 0.4])
                sphere(d = body_width * 0.9);
            }

            // Armored shell segments
            for(i = [0:shell_segments-1]) {
                translate([
                    -body_length/2 + (i * body_length/(shell_segments-1)),
                    0,
                    body_height/2
                ]) {
                    rotate([90, 0, 0])
                    scale([1, 0.3, 1])
                    cylinder(h = segment_height, d1 = segment_width, d2 = segment_width * 0.8);
                }
            }

            // Side armor bands
            for(side = [-1, 1]) {
                for(i = [0:shell_segments-1]) {
                    translate([
                        -body_length/2 + (i * body_length/(shell_segments-1)),
                        side * body_width/2.5,
                        body_height/2
                    ]) {
                        rotate([60 * side, 0, 90])
                        scale([0.5, 1, 0.5])
                        cylinder(h = segment_height * 1.5, d1 = segment_width/3, d2 = segment_width/4);
                    }
                }
            }
        }

        // Head
        translate([body_length/2 + head_length/2, 0, leg_height + 5]) {
            // Snout
            color("tan")
            scale([1.5, 0.8, 0.6])
            sphere(d = head_width);

            // Ears
            translate([0, head_width/2, head_height/2]) {
                rotate([0, 45, 0])
                cylinder(h = 8 * scale_factor, d1 = 5 * scale_factor, d2 = 3 * scale_factor);
            }
            translate([0, -head_width/2, head_height/2]) {
                rotate([0, -45, 0])
                cylinder(h = 8 * scale_factor, d1 = 5 * scale_factor, d2 = 3 * scale_factor);
            }

            // Eyes
            translate([head_length/2 - 3, head_width/3, 2]) {
                sphere(d = 3 * scale_factor);
            }
            translate([head_length/2 - 3, -head_width/3, 2]) {
                sphere(d = 3 * scale_factor);
            }

            // Nose
            translate([head_length/2 + 2, 0, 0]) {
                sphere(d = 2 * scale_factor);
            }
        }

        // Tail
        translate([-body_length/2 - tail_length/2, 0, leg_height + 5]) {
            for(i = [0:tail_segments-1]) {
                translate([
                    -i * tail_length/tail_segments,
                    0,
                    -i * 2
                ]) {
                    rotate([0, 0, -i * 5])
                    scale([1 - i*0.08, 1 - i*0.08, 1 - i*0.08])
                    cylinder(h = tail_length/tail_segments + 2, d1 = 12 * scale_factor, d2 = 10 * scale_factor);
                }
            }
        }

        // Legs
        for(side = [-1, 1]) {
            // Front legs
            translate([body_length/3, side * body_width/2, 0]) {
                color("saddlebrown")
                cylinder(h = leg_height, d = leg_width);

                // Feet
                translate([0, 0, -1])
                scale([1.5, 2, 0.5])
                sphere(d = leg_width);
            }

            // Rear legs
            translate([-body_length/3, side * body_width/2, 0]) {
                cylinder(h = leg_height, d = leg_width);

                // Feet
                translate([0, 0, -1])
                scale([1.5, 2, 0.5])
                sphere(d = leg_width);
            }
        }

        // Claws
        for(side = [-1, 1]) {
            for(pos = [body_length/3, -body_length/3]) {
                for(claw = [-1, 0, 1]) {
                    translate([pos, side * (body_width/2 + 8), 0]) {
                        rotate([90, 0, 0])
                        translate([claw * 3, 0, -5])
                        cylinder(h = 5, d1 = 2, d2 = 0.5);
                    }
                }
            }
        }
    }
}

// Generate the model
armadillo();

