// Medium-complexity realistic bee for 3D printing
// Approx. 70 mm long

// Body parameters
body_r = 10;
thorax_r = 12;
head_r = 9;
ab_len = 30;
stinger_len = 7;

// Wing parameters
wing_len = 25;
wing_w = 8;
wing_t = 0.8;

// Leg parameters
leg_len = 18;
leg_seg = 6;
leg_t = 1.5;

// Antenna parameters
ant_len = 12;
ant_t = 1;

// Stripes
stripe_w = 3;
num_stripes = 4;

module body() {
    // Head
    translate([-ab_len/2 - head_r*0.7, 0, 0])
        sphere(r = head_r);

    // Thorax
    translate([0, 0, 0])
        sphere(r = thorax_r);

    // Abdomen
    difference() {
        union() {
            translate([ab_len/2, 0, 0])
                scale([1.3, 1, 1])
                    sphere(r = body_r);
            // Stinger
            translate([ab_len + body_r + stinger_len/2, 0, 0])
                cylinder(h = stinger_len, r1 = 0.5, r2 = 0, center = true);
        }
        // Stripes
        for(i=[1:num_stripes]) {
            rotate([0, 90, 0])
                translate([ab_len/2 + (i - num_stripes/2) * stripe_w*2, 0, 0])
                    cube([stripe_w, body_r*2.5, body_r*2.5], center = true);
        }
    }
}

module wing() {
    difference() {
        hull() {
            translate([0, -wing_w/2, 0])
                sphere(r = wing_t*2);
            translate([wing_len, 0, 0])
                sphere(r = wing_t*2);
            translate([0, wing_w/2, 0])
                sphere(r = wing_t*2);
        }
        // Wing veins
        for(i=[1:3]) {
            translate([wing_len/4 * i, 0, -1])
                cube([0.5, wing_w*0.8, wing_t+2], center = true);
        }
    }
}

module wings() {
    // Left wings
    translate([0, 0, thorax_r - 2])
        rotate([20, 0, 30])
            wing();
    translate([5, 0, thorax_r - 3])
        rotate([10, 0, 20])
            scale([0.8, 0.8, 1])
                wing();

    // Right wings (mirrored)
    mirror([0, 1, 0]) {
        translate([0, 0, thorax_r - 2])
            rotate([20, 0, 30])
                wing();
        translate([5, 0, thorax_r - 3])
            rotate([10, 0, 20])
                scale([0.8, 0.8, 1])
                    wing();
    }
}

module leg() {
    // Upper segment
    translate([0, 0, -leg_seg/2])
        cylinder(h = leg_seg, r = leg_t, center = true);

    // Joint
    translate([0, 0, -leg_seg])
        sphere(r = leg_t*1.2);

    // Lower segment
    translate([0, leg_seg/1.5, -leg_seg*1.5])
        rotate([30, 0, 0])
            cylinder(h = leg_seg, r = leg_t*0.8, center = true);
}

module legs() {
    // Front legs
    for(side=[-1, 1]) {
        translate([-ab_len/4, side*thorax_r*0.8, -thorax_r*0.5])
            rotate([0, 40, side*20])
                leg();

        // Middle legs
        translate([0, side*thorax_r*0.9, -thorax_r*0.7])
            rotate([0, 20, side*30])
                translate([0, 0, -2])
                    leg();

        // Rear legs
        translate([ab_len/3, side*body_r*0.9, -body_r*0.6])
            rotate([0, 10, side*40])
                translate([0, 0, -4])
                    leg();
    }
}

module antenna() {
    // Base
    translate([0, 0, 0])
        sphere(r = ant_t*1.5);

    // Main shaft
    translate([0, 0, -ant_len/2])
        cylinder(h = ant_len, r = ant_t, center = true);

    // Tip
    translate([0, 0, -ant_len])
        sphere(r = ant_t*0.8);
}

module antennas() {
    // Left antenna
    translate([-ab_len/2 - head_r*0.7, head_r*0.5, head_r*0.5])
        rotate([30, 0, -30])
            antenna();

    // Right antenna
    translate([-ab_len/2 - head_r*0.7, -head_r*0.5, head_r*0.5])
        rotate([30, 0, 30])
            antenna();
}

// Compound eyes
module eyes() {
    for(side=[-1, 1]) {
        translate([-ab_len/2 - head_r*0.5, side*head_r*0.6, head_r*0.3])
            sphere(r = head_r*0.4);
    }
}

// Assembly
union() {
    body();
    wings();
    legs();
    antennas();
    eyes();
}

