// Realistic Badger - Medium Complexity
// 3D printable, ~65mm long

module badger() {
    // Body parameters
    body_length = 45;
    body_height = 20;
    body_width = 25;

    // Head parameters
    head_length = 18;
    head_height = 15;
    head_width = 15;

    // Leg parameters
    leg_height = 12;
    leg_width = 6;

    // Tail parameters
    tail_length = 15;
    tail_height = 8;

    // Main body - oval shape
    union() {
        // Central body
        translate([0, 0, leg_height])
        scale([1, 0.8, 0.6])
        sphere(d=body_width);

        // Body extension
        translate([-body_length/3, 0, leg_height])
        cube([body_length, body_width*0.7, body_height], center=true);

        // Head
        translate([body_length/2 + 5, 0, leg_height + 5])
        rotate([0, -10, 0])
        scale([1.2, 1, 1])
        sphere(d=head_width);

        // Snout
        translate([body_length/2 + 15, 0, leg_height + 3])
        scale([1.5, 0.7, 0.7])
        sphere(d=10);

        // Legs
        for (x = [-body_length/3, body_length/3]) {
            for (y = [-body_width/3, body_width/3]) {
                translate([x, y, leg_height/2])
                cylinder(h=leg_height, d=leg_width, $fn=8);
            }
        }

        // Tail
        translate([-body_length/2 - tail_length/2, 0, leg_height + 5])
        rotate([0, 20, 0])
        scale([1, 0.6, 0.6])
        cylinder(h=tail_length, d=tail_height, $fn=12);

        // Ears
        for (y = [-head_width/3, head_width/3]) {
            translate([body_length/2 + 5, y, leg_height + head_height])
            rotate([0, -20, 0])
            cylinder(h=6, d1=4, d2=2, $fn=6);
        }

        // Stripes
        for (i = [1:3]) {
            translate([body_length/2 - i*5, 0, leg_height + body_height*0.8])
            rotate([90, 0, 0])
            scale([1, 0.3, 0.1])
            cylinder(h=body_width*1.2, d=8, $fn=3);
        }

        // Eyes
        for (y = [-head_width/4, head_width/4]) {
            translate([body_length/2 + 12, y, leg_height + 8])
            sphere(d=2);
        }

        // Nose
        translate([body_length/2 + 18, 0, leg_height + 3])
        sphere(d=3);
    }
}

// Generate the badger
badger();

