// Realistic Boa Constructoridae - Medium Complexity
// A 3D printable constrictor snake model

// Parameters
$fn = 32;
body_length = 80;
head_length = 12;
tail_taper = 0.3;
body_thickness = 6;
scale_size = 0.8;

// Snake body module - creates tapered cylinder segments
module snake_body_segment(length, start_diam, end_diam) {
    hull() {
        translate([0, 0, -length/2]) 
            sphere(d=start_diam);
        translate([0, 0, length/2]) 
            sphere(d=end_diam);
    }
}

// Snake scales pattern
module scales(diameter, length) {
    scale_count = ceil(length / (diameter * 0.6));

    for(i = [0:scale_count-1]) {
        z_pos = -length/2 + i * (length/scale_count);
        rotate([0, 0, i * 15]) 
            translate([0, diameter/2 - 0.3, z_pos])
                scale([0.3, 0.2, 0.1]) 
                    sphere(d=diameter);
    }
}

// Snake head with realistic features
module snake_head() {
    // Main head shape
    difference() {
        union() {
            // Head body
            scale([1.2, 0.8, 1]) 
                sphere(d=head_length);

            // Snout extension
            translate([head_length/2, 0, 0]) 
                scale([1, 0.8, 0.6]) 
                    sphere(d=head_length * 0.8);
        }

        // Eye sockets
        translate([head_length/3, head_length/3, head_length/4]) 
            sphere(d=head_length/3);
        translate([head_length/3, -head_length/3, head_length/4]) 
            sphere(d=head_length/3);

        // Nostrils
        translate([head_length * 0.7, head_length/4, 0]) 
            rotate([45, 0, 0]) 
                cylinder(h=head_length/4, d1=head_length/6, d2=0);
        translate([head_length * 0.7, -head_length/4, 0]) 
            rotate([45, 0, 0]) 
                cylinder(h=head_length/4, d1=head_length/6, d2=0);
    }

    // Eyes
    translate([head_length/3, head_length/3, head_length/4]) 
        color("black") 
            sphere(d=head_length/4);
    translate([head_length/3, -head_length/3, head_length/4]) 
        color("black") 
            sphere(d=head_length/4);
}

// Main body construction
module snake_body() {
    segments = 20;
    segment_length = body_length / segments;

    // Create tapered body segments
    for(i = [0:segments-1]) {
        t = i / (segments - 1);

        // Calculate diameters along body
        if(t < 0.1) {
            // Head transition
            diam1 = head_length * 0.8;
            diam2 = body_thickness;
        } else if(t > 0.85) {
            // Tail taper
            diam1 = body_thickness * (1 - (t - 0.85) * 4);
            diam2 = body_thickness * (1 - (t - 0.85) * 4 - 0.1);
        } else {
            // Main body
            diam1 = body_thickness;
            diam2 = body_thickness;
        }

        // Position segment along S-curve
        x_pos = sin(t * 3.14 * 2) * 15;
        y_pos = cos(t * 3.14 * 2) * 8;
        z_pos = -body_length/2 + i * segment_length;

        translate([x_pos, y_pos, z_pos]) {
            rotate([0, 90, 0]) {
                snake_body_segment(segment_length, diam1, diam2);

                // Add scales
                scales(diam1, segment_length);
            }
        }
    }
}

// Assembly
scale([scale_size, scale_size, scale_size]) {
    // Position head
    translate([0, 0, body_length/2 + head_length/2]) {
        rotate([0, 90, 0]) {
            snake_head();
        }
    }

    // Main body
    snake_body();

    // Forked tongue
    translate([body_length/2 + head_length - 1, 0, 0]) {
        rotate([0, 90, 0]) {
            // Split tongue
            translate([0, 0.5, 0]) 
                scale([0.1, 0.05, 1]) 
                    cube([1, 1, 2]);
            translate([0, -0.5, 0]) 
                scale([0.1, 0.05, 1]) 
                    cube([1, 1, 2]);
        }
    }
}

