// Helper module for rounded cylinders (beaks, legs)
module rounded_cylinder(h, r1, r2, center=false) {
    cylinder(h=h, r1=r1, r2=r2, center=center, $fn=20);
}

// Head with beak
module head() {
    union() {
        // Main head sphere
        translate([0, 0, 8]) 
            sphere(r=6, $fn=32);

