            sphere(r=1.2, $fn=16);

        // Eye ring
        translate([5, 3.5, 9])
            sphere(r=1.5, $fn=16);
    }
}

// Neck - slightly curved
module neck() {
    for(i = [0:3]) {
        translate([0, 0, i*3])
            rotate([0, i*3, 0])
            scale([1 - i*0.05, 1 - i*0.05, 1])
            sphere(r=4 - i*0.3, $fn=24);
    }
}

// Body with tapering shape
module body() {
    difference() {
        // Main body
        union() {
            // Upper body
            translate([0, 0, -15])
                scale([1.2, 0.8, 1.5])
                sphere(r=12, $fn=32);

            // Lower body
            translate([0, 0, -25])
                scale([1.1, 1, 1.2])
                sphere(r=10, $fn=32);
        }

        // Neck hole
        translate([0, 0, 5])
            rotate([0, 20, 0])
            cylinder(h=15, r=4, $fn=20);
    }
}

// Wing module
module wing() {
    difference() {
        // Main wing shape
        translate([-5, 8, -15])
            rotate([0, 10, 20])
            scale([1.5, 0.3, 1])
            sphere(r=12, $fn=24);

        // Wing tip rounding
        translate([5, 12, -20])
            sphere(r=8, $fn=16);
    }
}

// Tail
module tail() {
    union() {
        // Central tail feathers
        translate([-15, 0, -25])
            rotate([0, 0, 5])
            scale([0.3, 1, 0.1])
            cube([10, 8, 3]);

