// Realistic 3D Printable Butterfly - Medium Complexity

// Overall dimensions
wing_span = 70;
body_length = 40;
body_diameter = 6;

module wing_base() {
    difference() {
        // Main wing shape
        scale([1, 0.7, 0.2])
        sphere(r = wing_span / 2);

        // Remove bottom half
        translate([0, 0, -wing_span / 2])
        cube([wing_span * 2, wing_span * 2, wing_span], center = true);
    }
}

module wing_detail() {
    // Vein pattern
    for (i = [0:30:150]) {
        rotate([0, 0, i])
        translate([0, 0, 0.5])
        scale([0.02, 1, 0.05])
        cylinder(h = wing_span * 0.4, r1 = 0, r2 = 1);
    }

    // Wing spots
    for (pos = [[-10, 5], [-15, 0], [-12, -5]]) {
        translate([pos[0], pos[1], 0.6])
        scale([1, 0.7, 0.05])
        sphere(r = 3);
    }
}

module left_wing() {
    translate([-body_diameter, 0, 0])
    union() {
        wing_base();
        wing_detail();
    }
}

module body_segment(height, radius, offset) {
    translate([0, 0, offset])
    cylinder(h = height, r = radius, $fn = 12);
}

module body() {
    union() {
        // Head
        translate([0, 0, body_length - 3])
        sphere(r = body_diameter * 0.8);

        // Thorax
        body_segment(15, body_diameter, 15);

        // Abdomen segments
        for (i = [0:2:12]) {
            scale([1, 1, 0.8])
            body_segment(2, body_diameter - i * 0.2, 12 - i);
        }
    }
}

module antenna() {
    translate([0, body_diameter * 0.7, body_length - 2])
    rotate([0, 45, 0])
    cylinder(h = 15, r = 0.3);

    translate([0, -body_diameter * 0.7, body_length - 2])
    rotate([0, -45, 0])
    cylinder(h = 15, r = 0.3);
}

module butterfly() {
    union() {
        // Left wing
        left_wing();

        // Right wing (mirrored)
        mirror([1, 0, 0])
        left_wing();

        // Body
        body();

        // Antennae
        antenna();
    }
}

// Add support base for 3D printing
translate([0, 0, -1])
difference() {
    cylinder(h = 2, r = 5, $fn = 20);
    cylinder(h = 2.1, r = 2, $fn = 20);
}

// Main butterfly
butterfly();

