// Canary bird - Realistic style, medium complexity

// Overall size: ~80mm long

$fn = 32;  // Smooth curves for 3D printing

module canary() {
    // Main body
    union() {
        // Body - egg shape
        scale([1.2, 1, 1.1])
        sphere(r=10, center=true);

        // Head - slightly smaller sphere
        translate([12, 0, 5])
        sphere(r=8, center=true);

        // Beak - cone shape
        translate([21, 0, 5])
        rotate([0, -90, 0])
        cylinder(h=8, r1=4, r2=0, center=true);

        // Wings - flattened ellipsoids
        // Left wing
        translate([0, -8, 2])
        rotate([20, 0, -30])
        scale([1.5, 0.4, 0.8])
        sphere(r=10, center=true);

        // Right wing
        translate([0, 8, 2])
        rotate([20, 0, 30])
        scale([1.5, 0.4, 0.8])
        sphere(r=10, center=true);

        // Tail - tapered cylinders
        for(i = [-20:5:-5]) {
            translate([i, 0, -1])
            rotate([0, 10 + (i+20)/2, 0])
            scale([0.8, 1 - abs(i)/30, 0.3])
            cylinder(h=3, r=5, center=true);
        }

        // Legs - thin cylinders
        // Left leg
        translate([-5, -3, -10])
        cylinder(h=8, r=1.5, center=false);

        // Right leg
        translate([-5, 3, -10])
        cylinder(h=8, r=1.5, center=false);

        // Feet - small spheres for toes
        for(side = [-1, 1]) {
            for(toe = [-1, 0, 1]) {
                translate([-5 + toe*2, side*5, -10])
                sphere(r=1.2, center=true);
            }
        }

        // Eyes - small spheres
        translate([16, -2.5, 7])
        sphere(r=1.5, center=true);

        translate([16, 2.5, 7])
        sphere(r=1.5, center=true);
    }
}

// Create the canary
canary();

