        // Long outer barbels
        translate([28, side*12, -3])
            rotate([0, side*20, 0])
                cylinder(h=15, r=1.2, center=true);

        // Short inner barbels
        translate([30, side*7, -2])
            rotate([0, side*15, 0])
                cylinder(h=8, r=1, center=true);
    }

    // Dorsal fin base
    translate([-5, 0, 8])
        rotate([90, 0, 0])
            cylinder(h=12, r1=2, r2=0.5, center=true);

    // Dorsal fin rays
    for(i = [-4:1:4]) {
        translate([-5 + i*1.5, 0, 8])
            rotate([90, i*10, 0])
                cylinder(h=10 - abs(i), r=0.8, center=true);
    }

    // Pectoral fins
    for(side = [-1, 1]) {
        translate([15, side*12, -3])
            rotate([0, side*45, 30])
                scale([1, 0.3, 0.1])
                    sphere(8, center=true);
    }

    // Tail
    translate([-28, 0, -2])
        rotate([0, 0, 0])
            union() {
                // Upper tail lobe
                translate([0, 0, 3])
                    rotate([30, 0, 0])
                        scale([1.5, 0.3, 0.1])
                            sphere(12, center=true);

                // Lower tail lobe
                translate([0, 0, -3])
                    rotate([-30, 0, 0])
                        scale([1.5, 0.3, 0.1])
                            sphere(12, center=true);

                // Tail connection
                cylinder(h=10, r1=8, r2=4, center=true);
            }

    // Eyes
    for(side = [-1, 1]) {
        translate([30, side*8, 2]) {
            // Eye socket
            difference() {
                sphere(3, center=true);
                translate([1, 0, 0])
                    sphere(2.5, center=true);
            }
            // Pupil
            translate([2, 0, 0])
                color("black")
                    sphere(1.2, center=true);
        }
