// Centipede body segments
module body_segment(radius = 5, height = 8) {
    hull() {
        // Main body cylinder
        cylinder(h = height, r = radius);
        // Slightly tapered tail end
        translate([0, 0, height])
        cylinder(h = height/3, r = radius * 0.8);
    }
}

// Centipede leg pair
module leg_pair(segment_radius) {
    leg_length = segment_radius * 1.5;
    leg_thickness = segment_radius * 0.2;

    // Left leg
    translate([-segment_radius * 0.7, 0, 0])
    rotate([0, 30, 0])
    cylinder(h = leg_length, r = leg_thickness);

    // Right leg
    translate([segment_radius * 0.7, 0, 0])
    rotate([0, -30, 0])
    cylinder(h = leg_length, r = leg_thickness);
}

// Centipede head
module head(radius = 6) {
    // Main head sphere
    sphere(r = radius);

    // Antennae
    antenna_length = radius * 1.5;
    antenna_thickness = radius * 0.1;

    // Left antenna
    translate([-radius * 0.5, 0, radius * 0.8])
    rotate([45, 0, 0])
    cylinder(h = antenna_length, r = antenna_thickness);

    // Right antenna
    translate([radius * 0.5, 0, radius * 0.8])
    rotate([45, 0, 0])
    cylinder(h = antenna_length, r = antenna_thickness);
}

// Complete centipede
module centipede() {
    segments = 12;
    segment_spacing = 7;
    curve_radius = 30;

    // Create curved body path
    for (i = [0:segments-1]) {
        angle = i * 15;
        x_pos = curve_radius * cos(angle);
        y_pos = curve_radius * sin(angle);
        z_pos = 0;

        translate([x_pos, y_pos, z_pos])
        rotate([0, 0, angle])
        union() {
            // Body segment
            color("Brown")
            body_segment();

            // Legs for this segment
            color("DarkRed")
            for (z = [2:2:6]) {
                translate([0, 0, z])
                leg_pair(5);
            }
        }
    }

    // Add head at the front
    first_angle = 0;
    head_x = curve_radius * cos(first_angle);
    head_y = curve_radius * sin(first_angle);

    translate([head_x, head_y, 0])
    rotate([0, 0, first_angle])
    color("DarkBrown")
    head();
}

// Render the centipede
centipede();

