// Centipede - Realistic Style, Medium Complexity
// 3D printable model ~80mm long

// Body parameters
body_segments = 20;
segment_length = 3.5;
segment_width = 4;
segment_height = 3;
body_curve = 30; // degrees of overall curve

// Head parameters
head_length = 8;
head_width = 6;
head_height = 5;

// Leg parameters
legs_per_side = 2;
leg_length = 10;
leg_width = 1.5;
leg_thickness = 1;

// Antenna parameters
antenna_length = 12;
antenna_thickness = 0.8;

// Body wall thickness for 3D printing
wall_thickness = 1.2;

module centipede_body() {
    // Create main body with segments
    for (i = [0:body_segments-1]) {
        // Calculate position along curved body
        angle = body_curve * (i - body_segments/2) / body_segments;
        x = i * segment_length * cos(angle) * 0.9;
        y = i * segment_length * sin(angle) * 0.3;
        z = 0;

        // Create segment with realistic ellipsoid shape
        translate([x, y, z]) {
            rotate([0, angle, 0]) {
                // Main segment body
                scale([1, 0.8, 0.7]) 
                    sphere(d=segment_width, $fn=20);

                // Segment ridges for texture
                for (j = [0:2]) {
                    translate([0, 0, -segment_height/2 + j*1.5])
                        scale([1, 1.1, 0.2])
                            sphere(d=segment_width*0.9, $fn=16);
                }
            }
        }
    }
}

module centipede_head() {
    // Position head at front
    translate([-head_length/2 - 5, 0, 0]) {
        // Main head shape - elongated oval
        scale([1.3, 1, 1])
            sphere(d=head_width, $fn=20);

        // Mandibles
        translate([head_length/2, head_width/3, -head_height/3])
            rotate([0, -20, 30])
                scale([0.3, 0.3, 1])
                    cylinder(h=leg_length/2, d=leg_width*1.5, $fn=8);

        translate([head_length/2, -head_width/3, -head_height/3])
            rotate([0, 20, -30])
                scale([0.3, 0.3, 1])
                    cylinder(h=leg_length/2, d=leg_width*1.5, $fn=8);

        // Eyes
        translate([head_length/2 - 2, head_width/3, head_height/4])
            sphere(d=1.5, $fn=10);

        translate([head_length/2 - 2, -head_width/3, head_height/4])
            sphere(d=1.5, $fn=10);
    }
}

module centipede_antennae() {
    // Left antenna
    translate([-head_length - 2, head_width/2, head_height/4]) {
        rotate([0, 20, -20])
            cylinder(h=antenna_length, d=antenna_thickness, $fn=8);
        // Antenna segments
        for (k = [1:3]) {
            translate([0, 0, k*antenna_length/4])
                rotate([0, 0, k*15])
                    cylinder(h=antenna_length/4, d=antenna_thickness, $fn=8);
        }
    }

    // Right antenna
    translate([-head_length - 2, -head_width/2, head_height/4]) {
        rotate([0, 20, 20])
            cylinder(h=antenna_length, d=antenna_thickness, $fn=8);
        for (k = [1:3]) {
            translate([0, 0, k*antenna_length/4])
                rotate([0, 0, -k*15])
                    cylinder(h=antenna_length/4, d=antenna_thickness, $fn=8);
        }
    }
}

module centipede_leg(side, segment) {
    // Calculate segment position
    angle = body_curve * (segment - body_segments/2) / body_segments;
    x = segment * segment_length * cos(angle) * 0.9;
    y = segment * segment_length * sin(angle) * 0.3;

    // Create leg with joints
    translate([x, y, 0]) {
        rotate([0, angle, 0]) {
            // Coxa (first leg segment)
            translate([0, side * segment_width/2, -segment_height/2]) {
                rotate([0, 90, 0])
                    cylinder(h=leg_width, d=leg_thickness, $fn=8);

                // Main leg segments
                translate([side * leg_width/2, 0, -leg_width/2]) {
                    rotate([0, 30, 0])
                        cylinder(h=leg_length/3, d=leg_thickness, $fn=8);

                    translate([0, 0, -leg_length/3])
                        rotate([0, -30, 0])
                            cylinder(h=leg_length/3, d=leg_thickness*0.8, $fn=8);

                    translate([0, 0, -2*leg_length/3])
                        rotate([0, -30, 0])
                            cylinder(h=leg_length/3, d=leg_thickness*0.6, $fn=8);
                }
            }
        }
    }
}

// Main assembly
union() {
    // Body segments
    centipede_body();

    // Head
    centipede_head();

    // Antennae
    centipede_antennae();

    // Legs - create pairs for each segment
    for (segment = [2:3:body_segments-1]) {
        centipede_leg(1, segment);  // Right side
        centipede_leg(-1, segment); // Left side
    }
}

