module body_segment(index) {
    segment_width = segment_radius * 2;

    // Main segment body
    difference() {
        // Elliptical segment
        scale([1.2, 1, 0.8]) 
            sphere(r = segment_radius);

        // Bottom flat for printing
        translate([0, 0, -segment_radius]) 
            cube([segment_width * 2, segment_width * 2, segment_radius], center = true);
    }

    // Segment ridges for realism
    for (j = [0:3]) {
        rotate([0, 0, j * 90]) 
            translate([segment_radius * 0.3, 0, segment_radius * 0.5])
                scale([0.3, 0.1, 0.3]) 
                    sphere(r = segment_radius * 0.8);
    }

    // Spiracles (breathing holes)
    translate([0, segment_radius * 0.7, segment_radius * 0.2])
        sphere(r = 0.5);
    translate([0, -segment_radius * 0.7, segment_radius * 0.2])
        sphere(r = 0.5);
