// Chicken - Realistic Medium Complexity
// 3D printable chicken model ~70mm long

// Main parameters
body_len = 40;
body_h = 25;
body_w = 30;

head_len = 18;
head_h = 15;
head_w = 14;

leg_len = 20;
leg_r = 3;
foot_len = 12;
foot_w = 6;

wing_len = 25;
wing_w = 12;
wing_t = 2;

tail_len = 18;
tail_h = 15;

beak_len = 8;
beak_w = 4;
beak_h = 3;

comb_h = 6;
comb_w = 3;

eye_r = 2;

// Wall thickness for printing
wall = 1.5;

module chicken() {
    union() {
        // Body - egg-like shape
        difference() {
            scale([1, 0.8, 1]) sphere(d=body_w);
            translate([0, 0, -body_h/2]) cube([body_len, body_w, body_h], center=true);
        }
        translate([0, 0, -body_h/4]) cube([body_len*0.9, body_w*0.7, body_h*0.8], center=true);

        // Head
        translate([body_len/2 + head_len/3, 0, body_h/3]) 
            scale([1.2, 1, 1]) sphere(d=head_w);

        // Beak
        translate([body_len/2 + head_len/2 + beak_len/2, 0, body_h/3]) 
            rotate([0, -20, 0]) 
            scale([1, 0.7, 0.7]) 
            cylinder(h=beak_len, r1=beak_w/2, r2=0);

        // Comb - three parts
        for(i = [-1:1]) {
            translate([body_len/2 + head_len/2, i*comb_w, body_h/3 + head_h/2]) 
                rotate([90, 0, 0]) 
                cylinder(h=comb_h, r=comb_w/2);
        }

        // Wattles
        translate([body_len/2 + head_len/2, -head_w/3, body_h/3 - head_h/3]) 
            scale([1, 0.5, 0.7]) sphere(d=6);
        translate([body_len/2 + head_len/2, head_w/3, body_h/3 - head_h/3]) 
            scale([1, 0.5, 0.7]) sphere(d=6);

        // Eyes
        translate([body_len/2 + head_len/3, -head_w/3, body_h/3 + head_h/4]) 
            sphere(d=eye_r);
        translate([body_len/2 + head_len/3, head_w/3, body_h/3 + head_h/4]) 
            sphere(d=eye_r);

        // Wings - curved shape
        for(side = [-1, 1]) {
            translate([0, side*body_w/3, body_h/4]) {
                rotate([0, 0, side*10]) {
                    rotate([0, 90, 0]) {
                        // Main wing
                        difference() {
                            scale([1, 1.2, 1]) cylinder(h=wing_len, r=wing_w/2);
                            translate([0, 0, -wing_t]) 
                                scale([0.9, 1.1, 1]) 
                                cylinder(h=wing_len+2, r=wing_w/2-wing_t);
                        }

                        // Wing feathers
                        for(i = [0:2]) {
                            translate([0, 0, -wing_len/2 + i*wing_len/3]) 
                                rotate([0, i*15-15, 0]) 
                                scale([0.3, 1, 0.1]) 
                                cube([wing_len/3, wing_w, wing_t], center=true);
                        }
                    }
                }
            }
        }

        // Tail - fan shape
        for(i = [-2:2]) {
            rotate([0, 0, i*20]) {
                translate([-body_len/2 - tail_len/2, 0, body_h/3]) 
                    rotate([0, -30, 0]) 
                    scale([1, 0.3, 1]) 
                    cylinder(h=tail_len, r1=tail_h/2, r2=0);
            }
        }

        // Legs
        for(side = [-1, 1]) {
            translate([0, side*body_w/4, -body_h/2]) {
                // Upper leg
                cylinder(h=leg_len/2, r=leg_r);

                // Lower leg
                translate([0, 0, -leg_len/2]) {
                    rotate([15, 0, 0]) {
                        cylinder(h=leg_len/2, r=leg_r*0.8);

                        // Foot
                        translate([0, 0, -leg_len/2 - foot_len/2]) {
                            rotate([90, 0, 0]) {
                                // Main foot
                                cube([foot_len, foot_w, leg_r*0.8], center=true);

                                // Toes
                                for(j = [-1, 0, 1]) {
                                    translate([foot_len/2, j*foot_w/3, -leg_r/2]) 
                                        cube([foot_len/3, foot_w/4, leg_r*0.6]);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Generate chicken centered at origin
translate([0, 0, 25]) 
    chicken();

