    // Central body - elongated oval
    scale([2.5, 1, 0.8])
        sphere(10, $fn=32);
}

// Head with distinctive coelacanth features
module head() {
    union() {
        // Main head shape
        translate([20, 0, 0])
            scale([1.2, 0.9, 0.7])
                sphere(8, $fn=32);

        // Snout extension
        translate([28, 0, -1])
            scale([1.5, 0.6, 0.4])
                sphere(4, $fn=16);

        // Lower jaw
        translate([20, 0, -4])
            rotate([15, 0, 0])
                scale([1.1, 0.7, 0.3])
                    sphere(7, $fn=24);
    }
}

// Lobed fins - distinctive coelacanth feature
module lobed_fin(x_pos, y_pos, z_pos, angle, scale_factor) {
    translate([x_pos, y_pos, z_pos])
        rotate([0, 0, angle])
            scale([scale_factor, scale_factor, scale_factor])
                union() {
                    // Main fin lobe
                    translate([0, 0, 0])
                        scale([3, 1, 0.3])
                            sphere(3, $fn=16);

                    // Fin rays
                    for (i = [-2:1:2]) {
                        translate([2, i*1.5, -0.5])
                            scale([2, 0.3, 0.1])
                                rotate([0, i*10, 0])
                                    cylinder(h=4, r=0.5, $fn=8);
                    }
                }
}

// Tail fin - three-lobed
module tail_fin() {
    translate([-25, 0, 0])
        union() {
            // Upper lobe
            translate([0, 0, 3])
                rotate([30, 0, 0])
                    scale([1, 0.5, 0.2])
                        cylinder(h=10, r1=8, r2=2, $fn=16);

            // Middle lobe
            translate([0, 0, 0])
                scale([1, 0.3, 0.2])
                    cylinder(h=12, r1=6, r2=1, $fn=16);

            // Lower lobe
            translate([0, 0, -3])
                rotate([-30, 0, 0])
                    scale([1, 0.5, 0.2])
                        cylinder(h=10, r1=8, r2=2, $fn=16);
        }
}

// Dorsal fins
module dorsal_fins() {
    // First dorsal fin
    translate([-5, 0, 5])
        scale([0.8, 0.3, 1])
            cylinder(h=6, r1=4, r2=1, $fn=16);

    // Second dorsal fin
    translate([-15, 0, 5])
        scale([0.7, 0.3, 0.9])
