}

// Pectoral fins
module pectoral_fins() {
    // Left pectoral fin
    translate([10, 8, -2])
        lobed_fin(0, 0, 0, 90, 1);

    // Right pectoral fin
    translate([10, -8, -2])
        lobed_fin(0, 0, 0, -90, 1);
}

