    translate([-8, -5, -2])
        lobed_fin(0, 0, 0, -45, 0.7);
}

// Anal fin
module anal_fin() {
    translate([-20, 0, -4])
        lobed_fin(0, 0, 0, 0, 0.8);
}

// Scale texture pattern
module scale_texture() {
    // Simple scale pattern using small spheres
    for (i = [0:5:40]) {
        for (j = [-5:5:5]) {
            if (i*i + j*j < 400) {
