module rounded_cylinder(h, r1, r2, vertical=true) {
    // Helper: cylinder with half-sphere ends for organic joints
    $fn = 16;
    if (vertical) {
        translate([0,0,h/2]) cylinder(h=h, r1=r1, r2=r2);
        translate([0,0,h]) sphere(r2);
        translate([0,0,0]) sphere(r1);
    } else {
        rotate([0,90,0]) rounded_cylinder(h, r1, r2, vertical=true);
    }
}

module leg(x, y, angle, scale=1) {
    translate([x,y,0]) {
        rotate([0,0,angle]) {
            scale([scale,scale,scale]) {
                // Upper leg
                rounded_cylinder(10, 3.2, 2.2);
                // Lower leg
                translate([0,0,10]) rotate([10,0,0]) rounded_cylinder(12, 2.2, 1.8);
