module torso() {
    // Main body - elongated ellipsoid
    rotate([0, 10, 0])
    scale([1.2, 0.8, 0.7])
    sphere(d=body_height, $fn=32);

    // Chest protrusion
    translate([body_length/4, 0, -body_height/4])
    rotate([0, 15, 0])
    scale([0.4, 0.6, 0.5])
    sphere(d=body_height/2, $fn=24);
