module head() {
    // Skull
    translate([-body_length/2 - head_size/2, 0, body_height/3])
    rotate([0, -10, 0])
    sphere(d=head_size, $fn=32);

    // Muzzle
    translate([-body_length/2 - head_size, 0, body_height/3 - head_size/3])
    rotate([0, -10, 0])
    scale([0.7, 0.6, 0.5])
    sphere(d=head_size * 0.8, $fn=24);

    // Eyes
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_size/1.5, side * head_size/3, body_height/3 + head_size/6])
        sphere(d=head_size/8, $fn=16);
    }

    // Nose
    translate([-body_length/2 - head_size * 1.1, 0, body_height/3 - head_size/6])
    sphere(d=head_size/12, $fn=12);
}

module limbs() {
    // Arms
    for(side = [-1, 1]) {
        // Upper arm
        translate([-body_length/4, side * body_width/2, 0])
        rotate([0, 0, side * 30])
        rotate([45, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/3, d2=body_width/4, $fn=12);

        // Lower arm
        translate([-body_length/4 - limb_length/3, side * body_width/1.3, -limb_length/3])
        rotate([0, 0, side * 30])
        rotate([90, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/4, d2=body_width/5, $fn=12);
    }

    // Legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([body_length/3, side * body_width/2, -body_height/3])
        rotate([0, 0, side * -20])
        rotate([30, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/3, d2=body_width/4, $fn=12);

        // Lower leg
        translate([body_length/3 + limb_length/3, side * body_width/1.5, -body_height/2 - limb_length/3])
        rotate([0, 0, side * -20])
        rotate([90, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/4, d2=body_width/5, $fn=12);
    }
