// Cobra - realistic style, medium complexity
// Designed for 3D printing with 1.2mm walls and minimal overhangs

// Overall scale factor
scale_factor = 0.4;

// Head module
module cobra_head() {
    // Main head shape
    translate([0, 0, 5]) {
        scale([1.2, 0.8, 1]) sphere(8);
    }

    // Snout extension
    translate([8, 0, 4]) {
        scale([1.5, 0.7, 0.8]) sphere(4);
    }

    // Hood
    difference() {
        union() {
            // Left hood
            translate([-3, 0, 6]) {
                rotate([0, 0, -30]) 
                scale([2, 0.3, 1.5]) sphere(6);
            }
            // Right hood
            translate([3, 0, 6]) {
                rotate([0, 0, 30]) 
                scale([2, 0.3, 1.5]) sphere(6);
            }
        }

        // Remove back part of hood
        translate([0, -8, 6]) cube(20, center=true);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([4, side*3.5, 7]) {
            color("black") sphere(1);
        }
    }

    // Nostrils
    for(side = [-1, 1]) {
        translate([10, side*1.5, 5]) {
            scale([0.5, 0.5, 0.3]) sphere(1);
        }
    }
}

// Body segment generator
module body_segment(radius, length, z_pos) {
    translate([0, 0, z_pos]) {
        cylinder(h=length, r=radius, center=true);
    }
}

// Tail segment generator (tapered)
module tail_segment(radius, length, z_pos, taper) {
    translate([0, 0, z_pos]) {
        cylinder(h=length, r1=radius, r2=radius*taper, center=true);
    }
}

// Scale pattern on body
module scales(body_radius, segments) {
    for(i = [0:segments-1]) {
        angle = i * 30;
        z_pos = i * 2 - 20;

        // Top scales
        rotate([0, 0, angle]) {
            translate([0, body_radius * 0.8, z_pos]) {
                scale([0.3, 0.1, 0.1]) cube([3, 3, 1]);
            }
        }

        // Side scales
        rotate([0, 0, angle + 15]) {
            translate([0, body_radius * 0.9, z_pos + 1]) {
                scale([0.2, 0.1, 0.08]) cube([2, 2, 1]);
            }
        }
    }
}

// Main cobra assembly
module cobra() {
    scale(scale_factor) {
        union() {
            // Head
            cobra_head();

            // Body segments with decreasing radius
            body_segment(6, 4, 0);
            body_segment(5.5, 4, -4);
            body_segment(5, 4, -8);
            body_segment(4.5, 4, -12);
            body_segment(4, 4, -16);
            body_segment(3.5, 4, -20);

            // Tail segments
            tail_segment(3, 4, -24, 0.8);
            tail_segment(2.4, 4, -28, 0.7);
            tail_segment(1.7, 4, -32, 0.6);
            tail_segment(1, 4, -36, 0.5);
            tail_segment(0.5, 4, -40, 0.4);

            // Scales on body
            scales(5, 15);
        }
    }
}

// Create the cobra
cobra();

// Add simple base for stability
translate([0, 0, -23 * scale_factor]) {
    color("darkgreen") 
    cylinder(h=2 * scale_factor, r=25 * scale_factor);
