// Realistic branching coral
// Approx 75 mm tall, designed for 3D printing

$fn = 36;

// Main coral structure
difference() {
    union() {
        // Base rock
        translate([0, 0, -5])
            scale([1.2, 1.2, 0.6])
                sphere(10);

        // Main trunk
        translate([0, 0, 0])
            cylinder(h = 25, r1 = 8, r2 = 4);

        // Primary branches
        branch(0, 0, 25, 0, 0, 0, 0.9);
        branch(0, 0, 20, -20, 20, 15, 0.8);
        branch(0, 0, 18, 30, -15, 10, 0.8);
        branch(0, 0, 22, -10, -25, -20, 0.7);
    }

    // Hollow base for material saving
    translate([0, 0, -3])
        cylinder(h = 20, r1 = 7, r2 = 3, $fn = 24);
}

// Branch generation module
module branch(x, y, z, rot_x, rot_y, rot_z, scale_factor) {
    translate([x, y, z]) {
        rotate([rot_x, rot_y, rot_z]) {
            // Branch segment
            cylinder(h = 15 * scale_factor, r1 = 3 * scale_factor, r2 = 1.5 * scale_factor);

            // Secondary branches if scale factor is large enough
            if (scale_factor > 0.5) {
                translate([0, 0, 15 * scale_factor])
                    branch(0, 0, 0, -25 + rand(-10, 10), 20 + rand(-10, 10), 0, scale_factor * 0.7);
                translate([0, 0, 12 * scale_factor])
                    branch(0, 0, 0, 30 + rand(-10, 10), -15 + rand(-10, 10), 0, scale_factor * 0.6);
            }

            // Coral polyps at tips
            if (scale_factor <= 0.5) {
                translate([0, 0, 15 * scale_factor])
                    polyps(scale_factor);
            }
        }
    }
}

// Coral polyp cluster module
module polyps(scale_factor) {
    for (i = [0:7]) {
        rotate([0, 0, i * 45]) {
            translate([2 * scale_factor, 0, 0]) {
                rotate([90 + rand(-20, 20), 0, 0]) {
                    // Polyp body
                    cylinder(h = 2 * scale_factor, r1 = 0.8 * scale_factor, r2 = 0.4 * scale_factor);

                    // Polyp tentacles
                    for (j = [0:5]) {
                        rotate([0, 0, j * 60]) {
                            translate([0, 0, 2 * scale_factor]) {
                                rotate([0, 20 + rand(-10, 10), 0]) {
                                    cylinder(h = 1.5 * scale_factor, r1 = 0.2 * scale_factor, r2 = 0.1 * scale_factor);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Random number generator for natural variation
function rand(min, max) = min + (max - min) * (rands(0, 1, 1)[0]);

