// Crocodile model - medium complexity realistic style
// Dimensions: ~80mm length, 25mm height, 18mm width
// Wall thickness: 2mm for 3D printing

// Head module with snout details
module head() {
    // Main head base
    translate([-15, 0, 3]) rotate([0, 0, 5]) 
    scale([1.2, 0.7, 0.6]) sphere(8, $fn=8);

    // Snout extension
    translate([-25, 0, 2.5]) scale([1.8, 0.5, 0.4]) sphere(7, $fn=8);

    // Eye sockets
    translate([-12, 5, 6]) sphere(2, $fn=8);
    translate([-12, -5, 6]) sphere(2, $fn=8);

    // Nostrils
    translate([-28, 2.5, 5]) cylinder(h=2, r1=1, r2=0.5);
    translate([-28, -2.5, 5]) cylinder(h=2, r1=1, r2=0.5);
}

// Body segments with scutes
module body_segment(pos, scale_factor) {
    translate(pos) scale([scale_factor, 1, 0.8]) {
        // Main body segment
        sphere(6, $fn=8);

        // Scutes (armor plates) along back
        for(i = [-3:3]) {
            translate([0, i*2, 3]) cube([3, 1.5, 1]);
        }
    }
}

// Leg module
module leg(pos, angle, scale_factor) {
    translate(pos) rotate([0, 0, angle]) {
        // Upper leg
        translate([0, 0, -1]) rotate([-90, 0, 0]) 
        cylinder(h=8*scale_factor, r1=3*scale_factor, r2=2*scale_factor);

        // Lower leg
        translate([0, 0, -8*scale_factor-1]) rotate([-90, 0, 0])
        cylinder(h=6*scale_factor, r1=2*scale_factor, r2=1.5*scale_factor);

        // Foot
        translate([0, -6*scale_factor-1, -14*scale_factor]) 
        scale([1.5, 2, 0.5]) sphere(2*scale_factor, $fn=6);
    }
}

// Tail segments
module tail_segment(pos, scale_factor, angle) {
    translate(pos) rotate([0, 0, angle]) scale([scale_factor, scale_factor, scale_factor]) {
        // Main tail segment
        cube([8, 6, 4], center=true);

        // Tail ridge
        translate([0, 0, 2.5]) cube([8, 3, 1], center=true);
    }
}

// Teeth module
module teeth(pos, count) {
    for(i = [1:count]) {
        translate([pos[0]-i*2, pos[1], pos[2]]) 
        rotate([0, 20, 0]) cylinder(h=2, r1=0.5, r2=0.1);
    }
}

// Main assembly
union() {
    // Head
    head();

    // Teeth along jaws
    teeth([-20, 3, 2], 6);
    teeth([-20, -3, 2], 6);

    // Body segments with decreasing size
    body_segment([-5, 0, 3], 1);
    body_segment([5, 0, 3], 0.9);
    body_segment([15, 0, 2.8], 0.8);
