    tail_segment([42, 0, 1.8], 0.5, 5);
    tail_segment([48, 0, 1.6], 0.4, 10);
    tail_segment([53, 0, 1.4], 0.3, 15);
    tail_segment([57, 0, 1.2], 0.25, 20);

    // Legs - front
    leg([-5, 8, 0], 45, 0.8);
    leg([-5, -8, 0], -45, 0.8);

