// Realistic 3D printable crab – medium complexity
// Overall size ≈ 80 mm across claws

// crab body length = 50 mm
body_len = 50;
// body width = 40 mm
body_w   = 40;
// claw reach adds 15 mm each side
claw_reach = 15;

// Wall thickness for printing
wall = 1.5;

// Main module
module crab() {
    // Whole crab pivoted slightly for printing
    rotate([0,15,0])
    union() {
        body();
        legs();
        claws();
        eyes();
    }
}

// Main cephalothorax shell
module body() {
    difference() {
        // Upper domed shell
        scale([1, 0.8, 0.6])
            sphere(d = body_w, $fn = 64);
        // Flat underside
        translate([0,0,-body_w*0.3])
            cube([body_w*1.5, body_w*1.5, body_w*0.6], center=true);
        // Hollow inside
        scale([1-wall/body_w*2, 0.8-wall/body_w*2, 0.6-wall/body_w*2])
            sphere(d = body_w-wall, $fn = 64);
    }
    // Small abdomen plate under rear
    translate([0,-body_w*0.4, -body_w*0.25])
        scale([0.5, 0.3, 0.2])
            sphere(d = body_w*0.6, $fn = 32);
}

// Ten legs: 2 claws + 8 walking legs
// Use same segment module for all
module leg_segment(l=6, r=1.4) {
    cylinder(h=l, r=r, $fn=12);
}

// Create one walking leg
module walking_leg(side, seg_len, angles) {
    translate([side*body_w/2, 0, 0])
    rotate([90, 0, angles[0]])
    leg_segment(seg_len, 1.2);
    translate([side*body_w/2, 0, 0])
    rotate([90, 0, angles[0]])
    translate([0,0,seg_len])
    rotate([angles[1], 0, 0])
    leg_segment(seg_len*0.9, 1);
    translate([side*body_w/2, 0, 0])
    rotate([90, 0, angles[0]])
    translate([0,0,seg_len])
    rotate([angles[1], 0, 0])
    translate([0,0,seg_len*0.9])
    rotate([angles[2], 0, 0])
    leg_segment(seg_len*0.7, 0.8);
}

// Create claws
module claw() {
    // Two-segment claw arm
    rotate([90,0,0])
    union() {
        // Upper pincer
        translate([0,0,6])
            rotate([0,20,0])
            scale([2.5,1,1])
                sphere(d=6, $fn=16);
        // Lower pincer
        translate([0,0,6])
            rotate([0,-20,0])
            scale([2.5,1,1])
                sphere(d=6, $fn=16);
        // Arm
        cylinder(h=12, r1=3.5, r2=2.2, $fn=16);
    }
}

// Assemble all legs
module legs() {
    // Rear four pairs
    for(i=[1:4])
        walking_leg( 1, 7, [i*15-30, 20, -30]);
    for(i=[1:4])
        walking_leg(-1, 7, [-i*15+30, 20, -30]);
}

// Assemble claws
module claws() {
    // Right claw
    translate([body_w/2+2, 0, 0])
    rotate([0,0,-30])
    claw();
    // Left claw
    translate([-body_w/2-2, 0, 0])
    rotate([0,0,30])
    mirror([1,0,0])
    claw();
}

// Eyes on short stalks
module eyes() {
    // Right eye
    translate([body_w/4, body_w/3, body_w*0.3])
    cylinder(h=3, r1=1.2, r2=0.8, $fn=12);
    translate([body_w/4, body_w/3, body_w*0.3+3])
    sphere(d=2.4, $fn=16);
    // Left eye
    translate([-body_w/4, body_w/3, body_w*0.3])
    cylinder(h=3, r1=1.2, r2=0.8, $fn=12);
    translate([-body_w/4, body_w/3, body_w*0.3+3])
    sphere(d=2.4, $fn=16);
}

// Render the crab
crab();

