// Realistic 3D printable cricket – medium complexity
// Approximate length: 70 mm (head to wing tip)

$fn = 30; // smooth curves

module cricket() {
    // Body segments
    color("darkolivegreen")
    union() {
        head();
        thorax();
        abdomen();
        legs();
        wings();
        antennae();
    }
}

module head() {
    // rounded capsule head
    translate([0, 0, 5])
    rotate([90, 0, 0])
    cylinder(h = 12, r1 = 7, r2 = 7, center = true);

    // compound eyes
    translate([5, 0, 6])
    sphere(d = 5);
    translate([-5, 0, 6])
    sphere(d = 5);

    // mandibles
    translate([3, -5, 3])
    rotate([0, 30, 0])
    cube([2, 6, 2], center = true);
    translate([-3, -5, 3])
    rotate([0, -30, 0])
    cube([2, 6, 2], center = true);
}

module thorax() {
    // prothorax
    translate([0, 0, -4])
    rotate([90, 0, 0])
    cylinder(h = 10, r1 = 6, r2 = 7, center = true);
}

module abdomen() {
    // six-segment abdomen tapering
    for (i = [0:5]) {
        height = -10 - i * 3.5;
        radius = 7 - i * 0.8;
        translate([0, 0, height])
        rotate([90, 0, 0])
        cylinder(h = 3.2, r1 = radius, r2 = radius - 0.3, center = true);
    }

    // ovipositor (female cricket)
    translate([0, 0, -32])
    rotate([0, 0, 0])
    cylinder(h = 12, r1 = 1, r2 = 0.5, center = true);
}

module legs() {
    // hind legs
    hind_leg(1);
    hind_leg(-1);

    // middle legs
    middle_leg(1);
    middle_leg(-1);

    // front legs
    front_leg(1);
    front_leg(-1);
}

module hind_leg(side) {
    // coxa
    translate([side * 7, 0, -7])
    rotate([0, 0, side * -20])
    cylinder(h = 5, r = 2, center = true);

    // femur
    translate([side * 12, side * -10, -12])
    rotate([90, 0, side * -30])
    cylinder(h = 20, r1 = 2, r2 = 1.5, center = true);

    // tibia
    translate([side * 18, side * -22, -22])
    rotate([90, 0, side * -35])
    cylinder(h = 25, r1 = 1.5, r2 = 1, center = true);

    // tarsi
    translate([side * 22, side * -35, -28])
    rotate([90, 0, side * -40])
    cylinder(h = 8, r = 0.6, center = true);
}

module middle_leg(side) {
    translate([side * 6, 0, -2])
    rotate([0, 0, side * -20])
    cylinder(h = 4, r = 1.5, center = true);

    translate([side * 9, side * -8, -7])
    rotate([90, 0, side * -25])
    cylinder(h = 15, r1 = 1.5, r2 = 1, center = true);

    translate([side * 12, side * -15, -11])
    rotate([90, 0, side * -30])
    cylinder(h = 6, r = 0.6, center = true);
}

module front_leg(side) {
    translate([side * 5, 0, 2])
    rotate([0, 0, side * -20])
    cylinder(h = 3, r = 1.2, center = true);

    translate([side * 7, side * -5, -1])
    rotate([90, 0, side * -20])
    cylinder(h = 10, r1 = 1.2, r2 = 0.8, center = true);

    translate([side * 9, side * -10, -3])
    rotate([90, 0, side * -25])
    cylinder(h = 5, r = 0.5, center = true);
}

module wings() {
    // tegmina (hardened forewings)
    translate([0, 0, -8])
    rotate([0, 0, 0])
    linear_extrude(height = 0.8)
    polygon(points = [[0,0], [30,3], [28,8], [5,5], [0,0]]);

    mirror([1, 0, 0])
    translate([0, 0, -8])
    rotate([0, 0, 0])
    linear_extrude(height = 0.8)
    polygon(points = [[0,0], [30,3], [28,8], [5,5], [0,0]]);
}

module antennae() {
    // left antenna
    translate([4, 0, 11])
    rotate([0, 0, 30])
    cylinder(h = 35, r = 0.4, center = true);

    // right antenna
    translate([-4, 0, 11])
    rotate([0, 0, -30])
    cylinder(h = 35, r = 0.4, center = true);
}

