}

// Module for the head
module head() {
    translate([body_length/2+8, 0, body_height/2]) {
        // Main head shape
        difference() {
            rotate([0, -15, 0])
                scale([0.9, 0.8, 0.7])
                sphere(d=head_size);

            // Eye sockets
            translate([head_size/3, head_size/3, 0])
                sphere(d=head_size/3);
            translate([head_size/3, -head_size/3, 0])
                sphere(d=head_size/3);
