            rotate([0, -30, 0])
            scale([1.5, 0.3, 0.3])
            cylinder(h=head_size, d1=head_size/2, d2=0);
    }
}

// Module for legs and feet
module legs() {
    // Left leg
    translate([-body_length/3, body_height/2, -body_height/2]) {
