// Common Dace (Leuciscus leuciscus) - Medium complexity realistic model
// Total length ~80 mm, printable in one piece (no support needed)

$fn = 36;               // Smooth curves
body_length = 80;
body_height = 20;
body_width  = 12;

// Main body module
module dace_body() {
    // Tapered ellipsoid body
    difference() {
        scale([1, 0.6, 0.4])
            sphere(d = body_length);          // Main body shape

        // Tail cutout
        translate([body_length/2 - 2, 0, 0])
            cube([5, body_width*2, body_height*2], center = true);
    }
}

// Tail fin - forked
module tail_fin() {
    union() {
        // Upper lobe
        translate([body_length/2 - 2, 0, body_height/4])
            rotate([0, -20, 0])
            scale([0.8, 0.15, 0.6])
                sphere(d = 30);

        // Lower lobe
        translate([body_length/2 - 2, 0, -body_height/4])
            rotate([0, 20, 0])
            scale([0.8, 0.15, 0.6])
                sphere(d = 30);

        // Tail root
        translate([body_length/2 - 5, 0, 0])
            scale([0.3, 0.15, 0.3])
                sphere(d = 15);
    }
}

// Dorsal fin
module dorsal_fin() {
    for(i = [0:3]) {
        translate([body_length/2 - 20 - i*5, 0, body_height/2])
            rotate([0, 0, 10 + i*5])
            scale([0.2, 0.05, 1])
                cylinder(h = 10, d1 = 8, d2 = 2);
    }
}

// Anal fin
module anal_fin() {
    for(i = [0:2]) {
        translate([body_length/2 - 25 - i*4, 0, -body_height/2])
            rotate([0, 0, -10 - i*5])
            scale([0.2, 0.05, 1])
                cylinder(h = 8, d1 = 6, d2 = 2);
    }
}

// Pectoral fin (left side)
module pectoral_fin() {
    translate([-body_length/4, body_width/2 + 2, -body_height/4])
        rotate([0, 0, 45])
        scale([0.3, 0.1, 0.6])
            sphere(d = 15);
}

// Head with mouth
module head() {
    difference() {
        // Head shape
        translate([-body_length/2 + 5, 0, 0])
            scale([0.8, 0.7, 0.7])
                sphere(d = 20);

        // Mouth opening
        translate([-body_length/2 + 2, 0, -body_height/6])
            rotate([0, 20, 0])
            scale([0.5, 0.3, 0.3])
                cube([5, 3, 2], center = true);
    }

    // Eye
    translate([-body_length/2 + 8, body_width/3, body_height/4])
        sphere(d = 3);

    // Eye pupil
    color("black")
    translate([-body_length/2 + 9.5, body_width/3, body_height/4])
        sphere(d = 1.5);
}

// Lateral line (series of scales)
module lateral_line() {
    for(i = [0:10]) {
        translate([-body_length/2 + 15 + i*5, 0, 0])
            scale([0.1, 1, 0.3])
                sphere(d = 2);
    }
}

// Main assembly
union() {
    dace_body();
    tail_fin();
    dorsal_fin();
    anal_fin();
    pectoral_fin();
    mirror([0,1,0]) pectoral_fin();  // Right pectoral fin
    head();
    lateral_line();
}

