// Realistic Dingo - Medium Complexity
// Basic dimensions in mm
body_length = 70;
body_height = 35;
body_width = 25;
head_length = 25;
head_height = 20;
leg_height = 25;
leg_width = 8;
tail_length = 40;
tail_width = 8;

// Wall thickness for 3D printing
wall = 2;

module dingo_body() {
    // Main torso - elongated ellipsoid
    translate([0, 0, leg_height + body_height/2])
    scale([1, 0.6, 0.8])
    sphere(d=body_width, $fn=32);

    // Chest extension
    translate([body_length/4, 0, leg_height + body_height/3])
    scale([0.8, 0.5, 0.6])
    sphere(d=body_width*1.2, $fn=32);

    // Belly clearance
    translate([0, 0, leg_height/2])
    cube([body_length*1.2, body_width*0.8, leg_height], center=true);
}

module dingo_head() {
    // Main head shape
    translate([-body_length/2 - head_length/2, 0, leg_height + body_height*0.8])
    rotate([0, 15, 0])
    scale([1.2, 0.8, 1])
    sphere(d=head_height, $fn=32);

    // Snout extension
    translate([-body_length/2 - head_length, 0, leg_height + body_height*0.7])
    rotate([0, 10, 0])
    scale([1.5, 0.5, 0.6])
    sphere(d=head_height*0.8, $fn=32);

    // Ears (pointed, upright)
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_length/3, side*head_height/2, leg_height + body_height + head_height/2])
        rotate([side*10, 0, 0])
        cylinder(h=head_height*0.8, d1=head_height*0.3, d2=head_height*0.1, $fn=8);
    }
}

module dingo_legs() {
    // Front legs
    for(x = [body_length/4, -body_length/4]) {
        translate([x, body_width/3, leg_height/2])
        cylinder(h=leg_height, d=leg_width, $fn=16);

        // Paws
        translate([x, body_width/3, 2])
        scale([1, 1.2, 0.5])
        sphere(d=leg_width, $fn=16);
    }

    // Hind legs
    for(x = [body_length/2 - 5, body_length/2 + 5]) {
        translate([x, -body_width/4, leg_height/2 + 3])
        rotate([10, 0, 0])
        cylinder(h=leg_height - 3, d=leg_width*1.2, $fn=16);

        // Paws
        translate([x, -body_width/4, 2])
        scale([1.2, 1.3, 0.5])
        sphere(d=leg_width*1.1, $fn=16);
    }
}

module dingo_tail() {
    // Curved tail
    translate([body_length/2 + tail_length/2, 0, leg_height + body_height/2])
    rotate([0, 0, -20])
    for(i = [0:5]) {
        translate([i*tail_length/6, 0, sin(i*30)*3])
        rotate([0, -i*3, 0])
        scale([1, 0.8 - i*0.1, 0.8 - i*0.1])
        sphere(d=tail_width, $fn=16);
    }
}

module dingo_features() {
    // Eyes
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_length*0.8, side*head_height/3, leg_height + body_height*0.9])
        sphere(d=2, $fn=8);
    }

    // Nose
    translate([-body_length/2 - head_length - 2, 0, leg_height + body_height*0.7])
    sphere(d=3, $fn=8);
}

// Assemble the dingo
union() {
    dingo_body();
    dingo_head();
    dingo_legs();
    dingo_tail();
    dingo_features();
}

