// Dory - Realistic Style, Medium Complexity (approx. 90 lines)
// Overall length ~80 mm, printable on most FDM machines

// Hull parameters
hull_length = 80;
hull_width = 20;
hull_height = 12;
wall_thickness = 1.2;

module dory_hull() {
    // Main hull body
    difference() {
        // Outer hull shape
        hull_profile();

        // Hollow interior
        translate([0, 0, -1])
            scale([0.85, 0.85, 0.9])
            hull_profile();
    }
}

module hull_profile() {
    // Use hull() of stacked rounded rectangles
    hull() {
        // Bow
        translate([-hull_length/2, 0, 0])
            scale([1, 0.4, 0.8])
            sphere(d = hull_width, $fn=24);

        // Mid section
        translate([0, 0, hull_height/2])
            cube([hull_length*0.8, hull_width, hull_height], center=true);

        // Stern
        translate([hull_length/2, 0, 0])
            scale([1, 0.5, 0.7])
            sphere(d = hull_width, $fn=24);
    }
}

module benches() {
    // Forward bench
    translate([-hull_length/4, 0, hull_height - wall_thickness])
        cube([hull_length*0.2, hull_width - wall_thickness*2, wall_thickness], center=true);

    // Aft bench
    translate([hull_length/4, 0, hull_height - wall_thickness])
        cube([hull_length*0.25, hull_width - wall_thickness*2, wall_thickness], center=true);
}

module transom() {
    // Stern transom plate
    difference() {
        translate([hull_length/2 - wall_thickness/2, 0, hull_height/2])
            cube([wall_thickness, hull_width*0.9, hull_height*0.9], center=true);

        // Drain plug hole
        translate([hull_length/2, 0, 2])
            rotate([0, 90, 0])
            cylinder(h = wall_thickness + 1, d = 2, $fn=12);
    }
}

module oarlocks() {
    // Generate 2 oarlocks
    for (y = [-hull_width/2 - 1, hull_width/2 + 1]) {
        translate([-hull_length/6, y, hull_height + 2])
            rotate([90, 0, 0])
            difference() {
                cylinder(h = 4, d = 3, $fn=12);
                translate([0, 0, -0.1])
                    cylinder(h = 4.2, d = 1.5, $fn=12);
            }
    }
}

module keel() {
    // Central keel for directional stability
    translate([0, 0, -1])
        cube([hull_length*0.9, wall_thickness, 2]);
}

module bow_handle() {
    // Rope handle on bow
    translate([-hull_length/2, 0, hull_height/2])
        rotate([90, 0, 0])
        difference() {
            cylinder(h = 4, d = 5, $fn=16);
            translate([0, 0, -0.1])
                cylinder(h = 4.2, d = 3, $fn=16);
        }
}

// Assembly
union() {
    dory_hull();
    benches();
    transom();
    oarlocks();
    keel();
    bow_handle();
}

