// Realistic dormouse model for 3D printing
// Medium complexity with anatomically-inspired features

$fn = 32;
body_length = 50;
body_width = 25;
body_height = 20;
head_length = 18;
head_width = 15;
head_height = 14;
tail_length = 45;
tail_diameter = 6;
leg_length = 12;
leg_diameter = 4;
ear_size = 6;
whisker_length = 12;

module dormouse() {
    union() {
        // Main body - elongated ellipsoid
        translate([0, 0, body_height/2])
        scale([1, 0.7, 0.5])
        sphere(d=body_length);

        // Head - sphere with slight elongation
        translate([-(body_length/2 + head_length/2 - 5), 0, body_height/2 + 5])
        scale([1.2, 1, 1])
        sphere(d=head_length);

        // Snout extension
        translate([-(body_length/2 + head_length + 2), 0, body_height/2 + 3])
        scale([0.8, 0.6, 0.5])
        sphere(d=8);

        // Ears - positioned on head
        for(side = [-1, 1]) {
            translate([-(body_length/2 + head_length/2), side * (head_width/2 - 2), body_height/2 + head_height/2])
            rotate([0, 20 * side, 0])
            scale([0.7, 1, 1.2])
            sphere(d=ear_size);
        }

        // Eyes - small spheres
        for(side = [-1, 1]) {
            translate([-(body_length/2 + head_length/2 + 2), side * (head_width/3), body_height/2 + 6])
            color("black")
            sphere(d=2);
        }

        // Nose - tiny sphere
        translate([-(body_length/2 + head_length + 5), 0, body_height/2 + 3])
        color("black")
        sphere(d=1.5);

        // Whiskers - thin cylinders
        for(side = [-1, 1]) {
            for(i = [0:2]) {
                translate([-(body_length/2 + head_length + 4), side * (head_width/3), body_height/2 + 2 - i*1.5])
                rotate([0, side * 15 + i*5, 90])
                cylinder(h=whisker_length, d=0.5);
            }
        }

        // Tail - tapered cylinder with slight curve
        for(i = [0:9]) {
            translate([body_length/2 + i*4, 0, body_height/2 - 2 - i*0.5])
            rotate([0, -i*2, 0])
            cylinder(h=5, d=tail_diameter - i*0.4);
        }

        // Front legs
        for(side = [-1, 1]) {
            translate([-(body_length/4), side * (body_width/2), body_height/2 - 5])
            rotate([90, 0, 0])
            cylinder(h=leg_length, d=leg_diameter);

            // Front paws
            translate([-(body_length/4), side * (body_width/2 + 2), body_height/2 - 5 - leg_length])
            sphere(d=leg_diameter * 1.2);
        }

        // Hind legs - larger and angled
        for(side = [-1, 1]) {
            translate([body_length/3, side * (body_width/2), body_height/2 - 5])
            rotate([100, 0, 15 * side])
            cylinder(h=leg_length * 1.2, d=leg_diameter * 1.3);

            // Hind paws
            translate([body_length/3 + 2, side * (body_width/2 + 3), body_height/2 - 5 - leg_length])
            scale([1.2, 1.5, 0.8])
            sphere(d=leg_diameter * 1.5);
        }

        // Fur texture - small spheres along back
        for(i = [0:10]) {
            for(j = [0:3]) {
                offset = (i % 2) * 2;
                translate([-(body_length/2) + i*5, -body_width/4 + j*4 + offset, body_height/2 + 10])
                sphere(d=1.5);
            }
        }
    }
}

// Generate the dormouse centered at origin
dormouse();

