            // Body core
            translate([0, 0, 10]) {
                scale([1, 0.7, 0.6]) sphere(d=40, $fn=32);
            }

            // Tail - tapered oval
            translate([-25, 0, 8]) {
                rotate([0, 20, 0]) {
