                scale([0.7, 0.8, 0.5]) sphere(d=30, $fn=24);
            }

            // Wing left
            translate([0, -15, 20]) {
                rotate([0, -20, -10]) {
                    difference() {
                        scale([1.2, 0.3, 0.4]) sphere(d=25, $fn=16);
                        translate([5, 0, 0]) cube([15, 10, 10], center=true);
                    }
                }
            }

            // Wing right (mirrored)
            translate([0, 15, 20]) {
                rotate([0, -20, 10]) {
                    difference() {
                        scale([1.2, 0.3, 0.4]) sphere(d=25, $fn=16);
                        translate([5, 0, 0]) cube([15, 10, 10], center=true);
                    }
                }
            }

            // Neck
            translate([20, 0, 25]) {
                rotate([0, -30, 0]) {
                    cylinder(h=15, d1=12, d2=10, $fn=16);
                }
            }

            // Head
            translate([30, 0, 35]) {
                scale([0.8, 0.8, 0.9]) sphere(d=20, $fn=32);
            }

            // Beak
            translate([40, 0, 33]) {
                rotate([0, -10, 0]) {
                    scale([1.5, 0.5, 0.4]) {
                        difference() {
                            sphere(d=12, $fn=8);
                            translate([-6, 0, 0]) cube([12, 8, 8], center=true);
                        }
                    }
                }
            }

            // Eyes
            // Left eye
            translate([35, -7, 38]) {
                sphere(d=4, $fn=8);
            }

            // Right eye
            translate([35, 7, 38]) {
                sphere(d=4, $fn=8);
            }
        }

        // Legs
        module leg(side) {
            translate([10, side * 8, 0]) {
                union() {
                    // Upper leg
                    translate([0, 0, 5]) {
                        rotate([0, -10, side * 20]) {
                            cylinder(h=10, d=6, $fn=12);
                        }
