// Dragonfly - realistic style, medium complexity
// Overall length ~80mm, wingspan ~70mm, thickness ~6mm

module dragonfly() {
    // Body parameters
    body_length = 40;
    body_thick = 5;

    // Head
    translate([body_length/2 + 4, 0, 0])
    rotate([0, 90, 0])
    scale([1, 1, 1.2])
    sphere(d=6, $fn=20);

    // Thorax (largest segment)
    translate([body_length/4, 0, 0])
    scale([2, 1, 1])
    sphere(d=body_thick*2, $fn=20);

    // Abdomen segments
    for(i=[-body_length/2:4:body_length/2-8]) {
        translate([i, 0, 0])
        scale([1 - abs(i)/body_length, 1 - abs(i)/body_length*0.5, 1])
        sphere(d=body_thick*(1 - abs(i)/body_length*0.3), $fn=16);
    }

    // Eyes
    for(side=[-1, 1]) {
        translate([body_length/2 + 4, side*2.5, 1.5])
        sphere(d=3, $fn=12);
    }

    // Wings - four wings with realistic venation
    module wing(x, y, rot, scale_factor) {
        translate([x, y, 0])
        rotate([0, 0, rot])
        scale([scale_factor, scale_factor, 1])
        linear_extrude(height=0.4)
        polygon(points=[
            [0, 0], [5, 8], [15, 12], [25, 10], [30, 5], [32, 0], [30, -5], 
            [25, -10], [15, -12], [5, -8], [0, 0]
        ]);
    }

    // Front wings
    wing(body_length/4, body_thick, 30, 1.2);
    wing(body_length/4, -body_thick, -30, 1.2);

    // Hind wings
    wing(-body_length/8, body_thick*1.5, 45, 1);
    wing(-body_length/8, -body_thick*1.5, -45, 1);

    // Wing support veins
    for(side=[-1, 1]) {
        for(w=[1, 2]) {
            angle = side * (w == 1 ? 30 : 45);
            len = w == 1 ? 25 : 20;
            translate([body_length/4 - (w-1)*6, 0, 0])
            rotate([0, 0, angle])
            cylinder(h=len, d1=0.6, d2=0.3, $fn=8);
        }
    }

    // Legs - 6 legs, 3 per side
    module leg(x, y, angle, length) {
        translate([x, y, -body_thick/2])
        rotate([0, 0, angle])
        rotate([90, 0, 0])
        cylinder(h=length, d=0.4, $fn=6);
    }

    for(side=[-1, 1]) {
        leg(body_length/4, side*body_thick, side*30, 8);
        leg(body_length/4 - 3, side*body_thick*0.8, side*45, 7);
        leg(body_length/4 + 3, side*body_thick*0.8, side*15, 7);
    }

    // Tail appendages
    translate([-body_length/2 - 4, 2, 0])
    rotate([0, 0, -30])
    cylinder(h=5, d1=0.8, d2=0.4, $fn=6);

    translate([-body_length/2 - 4, -2, 0])
    rotate([0, 0, 30])
    cylinder(h=5, d1=0.8, d2=0.4, $fn=6);
}

// Main assembly
dragonfly();

