// Eagle 3D Model - Realistic Medium Complexity
// Size: ~80mm wingspan

$fn = 32; // Smooth curves

module head() {
    // Main head sphere
    translate([0, 0, 15])
    sphere(d=18);

    // Beak
    difference() {
        translate([9, 0, 15])
        rotate([0, -20, 0])
        cylinder(h=20, d1=8, d2=2);
        // Beak notch
        translate([16, 0, 15])
        rotate([0, -20, 0])
        cube([8, 3, 3], center=true);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([5, side*6, 18])
        color("black")
        sphere(d=4);
    }
}

module neck() {
    // Neck connecting head to body
    translate([0, 0, 8])
    cylinder(h=10, d1=10, d2=15);
}

module body() {
    // Main body
    translate([0, 0, 0])
    scale([1.5, 1, 1.2])
    sphere(d=30);

    // Tail
    difference() {
        translate([-25, 0, -5])
        rotate([0, 10, 0])
        cube([30, 2, 15]);
        // Tail feather cuts
        for(i = [-1:1]) {
            translate([-25, i*8, -5])
            rotate([0, 10, 0])
            cube([30, 2, 15]);
        }
    }
}

module wing() {
    // Main wing structure
    difference() {
        union() {
            // Upper wing
            translate([0, 0, 0])
            rotate([0, 0, -10])
            scale([1, 2.5, 0.3])
            sphere(d=25);

            // Lower wing
            translate([-5, 15, -5])
            rotate([0, 0, -20])
            scale([0.8, 2, 0.2])
            sphere(d=20);

            // Wing tip
            translate([-10, 35, -8])
            rotate([0, 0, -30])
            cylinder(h=15, d1=8, d2=3);
        }

        // Wing feather cuts
        for(i = [0:3]) {
            translate([0, 5 + i*8, 2])
            rotate([0, 0, -10 - i*5])
            cube([20, 1, 5]);
        }
    }
}

module legs() {
    // Left leg
    translate([8, -5, -15])
    rotate([10, 0, 0])
    cylinder(h=15, d=4);

    // Right leg
    translate([8, 5, -15])
    rotate([10, 0, 0])
    cylinder(h=15, d=4);

    // Feet
    for(side = [-1, 1]) {
        translate([8, side*8, -30])
        rotate([0, 0, 0])
        scale([1, 2, 0.3])
        sphere(d=8);

        // Toes
        for(angle = [-30, 0, 30]) {
            translate([8, side*8, -30])
            rotate([0, 0, angle])
            translate([0, 6, 0])
            rotate([90, 0, 0])
            cylinder(h=8, d=2);
        }
    }
}

module eagle() {
    union() {
        // Body assembly
        body();
        neck();
        head();

        // Left wing
        translate([5, 0, 10])
        rotate([0, 0, 30])
        wing();

        // Right wing
        translate([5, 0, 10])
        rotate([0, 180, 30])
        wing();

        // Legs
        legs();
    }
}

// Scale for 3D printing
scale(0.8)
eagle();
